}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-display antialiased;
    font-feature-settings: "rlig" 1, "calt" 1;
  }

  h1, h2, h3, h4, h5, h6 {
    @apply font-sans font-semibold;
  }
}

@layer components {
  .glassmorphism {
    @apply bg-card backdrop-blur-lg border border-white/10;
    background-color: hsl(var(--card) / 0.7);
  }

  .gradient-bg {
    background: linear-gradient(135deg, hsl(225, 73%, 65%) 0%, hsl(258, 39%, 41%) 100%);
  }

  .gradient-text {
    background: linear-gradient(135deg, hsl(225, 73%, 65%) 0%, hsl(310, 87%, 78%) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .tool-card {
    @apply glassmorphism rounded-xl p-6 cursor-pointer transition-all duration-300 hover:scale-105 hover:shadow-2xl;
  }

  .tool-card:hover {
    box-shadow: 0 20px 25px -5px hsl(var(--primary) / 0.2), 0 10px 10px -5px hsl(var(--primary) / 0.1);
  }

  .feature-card {
    @apply glassmorphism rounded-lg p-6 text-center transition-all hover:scale-105;
  }

  .feature-card:hover {
    background-color: hsl(var(--card) / 0.9);
  }

  .floating-animation {
    animation: float 6s ease-in-out infinite;
  }

  .glow-effect {
    animation: glow 2s ease-in-out infinite alternate;
  }

  .slide-up-animation {
    animation: slide-up 0.5s ease-out;
  }

  .fade-in-animation {
    animation: fade-in 0.6s ease-out;
  }
}

@layer utilities {
  .text-gradient {
    @apply gradient-text;
  }
}

@keyframes float {
  0%, 100% { 
    transform: translateY(0px); 
  }
  50% { 
    transform: translateY(-20px); 
  }
}

@keyframes glow {
  from { 
    box-shadow: 0 0 5px hsl(225, 73%, 65%), 0 0 10px hsl(225, 73%, 65%), 0 0 15px hsl(225, 73%, 65%); 
  }
  to { 
    box-shadow: 0 0 10px hsl(225, 73%, 65%), 0 0 20px hsl(225, 73%, 65%), 0 0 30px hsl(225, 73%, 65%); 
  }
}

@keyframes slide-up {
  from { 
    opacity: 0; 
    transform: translateY(30px); 
  }
  to { 
    opacity: 1; 
    transform: translateY(0); 
  }
}

@keyframes fade-in {
  from { 
    opacity: 0; 
  }
  to { 
    opacity: 1; 
  }
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background-color: hsl(var(--muted) / 0.5);
}

::-webkit-scrollbar-thumb {
  @apply rounded-full;
  background-color: hsl(var(--primary) / 0.5);
}

::-webkit-scrollbar-thumb:hover {
  background-color: hsl(var(--primary) / 0.7);
}

/* Loading spinner */
.spinner {
  @apply animate-spin rounded-full border-4 border-primary border-t-transparent;
}

/* File upload area */
.upload-area {
  @apply border-2 border-dashed rounded-lg p-8 text-center transition-colors;
  border-color: hsl(var(--primary) / 0.3);
}

.upload-area:hover {
  border-color: hsl(var(--primary) / 0.5);
  background-color: hsl(var(--primary) / 0.05);
}

.upload-area.dragover {
  @apply border-primary;
  background-color: hsl(var(--primary) / 0.1);
}

