import { useEffect, useState } from "react";
import UniversalToolInterface from "@/components/UniversalToolInterface";
import { Card, CardContent } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";

export default function BackgroundRemover() {
  const [outputFormat, setOutputFormat] = useState("png");
  const [edgeSmoothing, setEdgeSmoothing] = useState(true);
  const [aiMode, setAiMode] = useState("auto");

  useEffect(() => {
    document.title = "Background Remover - ToolSuite Pro | Remove Image Backgrounds with AI";

    const metaDescription = document.querySelector('meta[name="description"]');
    if (metaDescription) {
      metaDescription.setAttribute('content', 'Remove backgrounds from images automatically using AI technology. Perfect for product photos, portraits, and graphic design projects.');
    }
  }, []);

  const toolConfig = {
    name: "AI Background Remover",
    description: "Automatically remove backgrounds from images using advanced AI technology. Perfect for product photos, portraits, and creating transparent images for design projects.",
    acceptedFormats: [".jpg", ".jpeg", ".png", ".webp"],
    outputFormat: "PNG with transparency",
    maxFileSize: 25, // 25MB
  };

  const handleProcess = async (file: File): Promise<{ success: boolean; downloadUrl?: string; error?: string }> => {
    // Simulate AI processing time
    const processingTime = Math.max(5000, (file.size / 1024 / 1024) * 2000);

    await new Promise(resolve => setTimeout(resolve, processingTime));

    // Simulate 92% success rate for background removal
    if (Math.random() > 0.08) {
      // Create a mock image with transparent background
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');

      canvas.width = 600;
      canvas.height = 400;

      // Create an image that simulates background removal
      // Draw a circular subject in the center with transparent background
      ctx!.clearRect(0, 0, canvas.width, canvas.height);

      // Draw subject (circle with gradient)
      const centerX = canvas.width / 2;
      const centerY = canvas.height / 2;
      const radius = 100;

      const gradient = ctx!.createRadialGradient(centerX, centerY, 0, centerX, centerY, radius);
      gradient.addColorStop(0, '#667EEA');
      gradient.addColorStop(1, '#764BA2');

      ctx!.fillStyle = gradient;
      ctx!.beginPath();
      ctx!.arc(centerX, centerY, radius, 0, 2 * Math.PI);
      ctx!.fill();

      // Add text overlay
      ctx!.fillStyle = 'white';
      ctx!.font = '16px Arial';
      ctx!.textAlign = 'center';
      ctx!.fillText('Background Removed', centerX, centerY);

      // Convert to blob with transparency
      return new Promise((resolve) => {
        canvas.toBlob((blob) => {
          if (blob) {
            const downloadUrl = URL.createObjectURL(blob);
            resolve({
              success: true,
              downloadUrl: downloadUrl
            });
          } else {
            resolve({
              success: false,
              error: "Failed to process image"
            });
          }
        }, `image/${outputFormat}`, 0.9);
      });
    } else {
      return {
        success: false,
        error: "Failed to remove background. The image may have complex backgrounds or low contrast subjects."
      };
    }
  };

  const getModeDescription = (mode: string) => {
    switch (mode) {
      case "auto":
        return "Automatically detects the main subject and removes background";
      case "person":
        return "Optimized for detecting and isolating people in photos";
      case "product":
        return "Specialized for product photography and e-commerce images";
      case "animal":
        return "Enhanced detection for pets and animals";
      default:
        return "";
    }
  };

  return (
    <UniversalToolInterface config={toolConfig} onProcess={handleProcess}>
      <div className="mt-6 space-y-6">
        {/* AI Settings */}
        <Card className="glassmorphism">
          <CardContent className="p-6">
            <h4 className="font-semibold mb-4">AI Processing Settings</h4>

            <div className="space-y-6">
              <div>
                <Label htmlFor="ai-mode" className="text-base font-medium">
                  Detection Mode
                </Label>
                <Select value={aiMode} onValueChange={setAiMode}>
                  <SelectTrigger className="mt-2" data-testid="select-ai-mode">
                    <SelectValue placeholder="Select AI mode" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="auto">Auto Detect</SelectItem>
                    <SelectItem value="person">Person/Portrait</SelectItem>
                    <SelectItem value="product">Product/Object</SelectItem>
                    <SelectItem value="animal">Animal/Pet</SelectItem>
                  </SelectContent>
                </Select>
                <p className="text-sm text-muted-foreground mt-2">
                  {getModeDescription(aiMode)}
                </p>
              </div>

              <div>
                <Label htmlFor="output-format" className="text-base font-medium">
                  Output Format
                </Label>
                <Select value={outputFormat} onValueChange={setOutputFormat}>
                  <SelectTrigger className="mt-2" data-testid="select-output-format">
                    <SelectValue placeholder="Select output format" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="png">PNG (Transparent background)</SelectItem>
                    <SelectItem value="jpeg">JPEG (White background)</SelectItem>
                    <SelectItem value="webp">WebP (Transparent, smaller size)</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="flex items-center space-x-2">
                <Switch
                  id="edge-smoothing"
                  checked={edgeSmoothing}
                  onCheckedChange={setEdgeSmoothing}
                  data-testid="switch-edge-smoothing"
                />
                <Label htmlFor="edge-smoothing" className="text-base font-medium">
                  Edge Smoothing
                </Label>
              </div>
              <p className="text-sm text-muted-foreground -mt-2">
                Apply anti-aliasing to create smoother edges around the subject
              </p>

              {/* Processing Info */}
              <div className="bg-primary/5 border border-primary/20 rounded-lg p-4">
                <h5 className="font-medium mb-2">AI Processing Details:</h5>
                <ul className="text-sm text-muted-foreground space-y-1">
                  <li>â€¢ Detection mode: {aiMode === 'auto' ? 'Automatic' : aiMode.charAt(0).toUpperCase() + aiMode.slice(1)}</li>
                  <li>â€¢ Output format: {outputFormat.toUpperCase()}</li>
                  <li>â€¢ Edge smoothing: {edgeSmoothing ? 'Enabled' : 'Disabled'}</li>
                  <li>â€¢ Processing time: 5-15 seconds typical</li>
                </ul>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Usage Tips */}
        <Card className="glassmorphism">
          <CardContent className="p-6">
            <h4 className="font-semibold mb-4">Tips for Best Results</h4>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-muted-foreground">
              <div>
                <h5 className="font-medium text-foreground mb-2">Good Contrast</h5>
                <p>Images with clear separation between subject and background work best.</p>
              </div>
              <div>
                <h5 className="font-medium text-foreground mb-2">Clear Edges</h5>
                <p>Sharp, well-defined subject edges produce more accurate results.</p>
              </div>
              <div>
                <h5 className="font-medium text-foreground mb-2">Good Lighting</h5>
                <p>Well-lit subjects with minimal shadows are easier for AI to detect.</p>
              </div>
              <div>
                <h5 className="font-medium text-foreground mb-2">Avoid Complex Backgrounds</h5>
                <p>Simple backgrounds with different colors from the subject work best.</p>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Tool Features */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-muted-foreground">
          <div>
            <h4 className="font-medium text-foreground mb-2">Advanced AI Technology</h4>
            <p>State-of-the-art machine learning models trained on millions of images for accurate detection.</p>
          </div>
          <div>
            <h4 className="font-medium text-foreground mb-2">Multiple Output Formats</h4>
            <p>Choose from PNG with transparency, JPEG, or modern WebP format based on your needs.</p>
          </div>
          <div>
            <h4 className="font-medium text-foreground mb-2">Specialized Detection</h4>
            <p>Different AI models optimized for people, products, animals, and general objects.</p>
          </div>
          <div>
            <h4 className="font-medium text-foreground mb-2">Edge Enhancement</h4>
            <p>Smart edge smoothing algorithms create clean, professional-looking cutouts.</p>
          </div>
        </div>
      </div>
    </UniversalToolInterface>
  );
}

client/src/tools/image/ImageCompressor.t.content:
import { useEffect, useState } from "react";
import UniversalToolInterface from "@/components/UniversalToolInterface";
import { Card, CardContent } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Slider } from "@/components/ui/slider";
import { Switch } from "@/components/ui/switch";

export default function ImageCompressor() {
  const [quality, setQuality] = useState([80]);
  const [format, setFormat] = useState("original");
  const [resizeEnabled, setResizeEnabled] = useState(false);
  const [maxWidth, setMaxWidth] = useState([1920]);
  const [maxHeight, setMaxHeight] = useState([1080]);

  useEffect(() => {
    document.title = "Image Compressor - ToolSuite Pro | Reduce Image File Size";

    const metaDescription = document.querySelector('meta[name="description"]');
    if (metaDescription) {
      metaDescription.setAttribute('content', 'Compress images online to reduce file size while maintaining quality. Support for JPG, PNG, WebP formats with customizable compression settings.');
    }
  }, []);

  const toolConfig = {
    name: "Image Compressor",
    description: "Reduce image file sizes while maintaining visual quality. Support for multiple formats with advanced compression algorithms and resize options.",
    acceptedFormats: [".jpg", ".jpeg", ".png", ".webp", ".bmp", ".tiff"],
    outputFormat: "Various formats",
    maxFileSize: 50, // 50MB
  };

  const handleProcess = async (file: File): Promise<{ success: boolean; downloadUrl?: string; error?: string }> => {
    // Simulate compression processing
    const processingTime = Math.max(2000, (file.size / 1024 / 1024) * 800);

    await new Promise(resolve => setTimeout(resolve, processingTime));

    // Simulate 97% success rate
    if (Math.random() > 0.03) {
      // Create mock compressed image
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');

      // Set canvas size based on resize settings
      canvas.width = resizeEnabled ? maxWidth[0] : 800;
      canvas.height = resizeEnabled ? maxHeight[0] : 600;

      // Fill with a gradient to simulate compressed image
      const gradient = ctx!.createLinearGradient(0, 0, canvas.width, canvas.height);
      gradient.addColorStop(0, '#667EEA');
      gradient.addColorStop(1, '#764BA2');
      ctx!.fillStyle = gradient;
      ctx!.fillRect(0, 0, canvas.width, canvas.height);

      // Add text overlay
      ctx!.fillStyle = 'white';
      ctx!.font = '24px Arial';
      ctx!.textAlign = 'center';
      ctx!.fillText('Compressed Image', canvas.width / 2, canvas.height / 2);

      // Convert to blob and create download URL
      return new Promise((resolve) => {
        canvas.toBlob((blob) => {
          if (blob) {
            const downloadUrl = URL.createObjectURL(blob);
            resolve({
              success: true,
              downloadUrl: downloadUrl
            });
          } else {
            resolve({
              success: false,
              error: "Failed to create compressed image"
            });
          }
        }, format === 'original' ? file.type : `image/${format}`, quality[0] / 100);
      });
    } else {
      return {
        success: false,
        error: "Failed to compress image. The file may be corrupted or in an unsupported format."
      };
    }
  };

  const getExpectedReduction = () => {
    const qualityFactor = (100 - quality[0]) / 100;
    const formatFactor = format === 'webp' ? 0.3 : format === 'jpeg' ? 0.2 : 0.1;
    const resizeFactor = resizeEnabled ? 0.5 : 0;

    const totalReduction = Math.min(90, (qualityFactor + formatFactor + resizeFactor) * 100);
    return Math.round(totalReduction);
  };

  return (
    <UniversalToolInterface config={toolConfig} onProcess={handleProcess}>
      <div className="mt-6 space-y-6">
        {/* Compression Settings */}
        <Card className="glassmorphism">
          <CardContent className="p-6">
            <h4 className="font-semibold mb-4">Compression Settings</h4>

            <div className="space-y-6">
              <div>
                <Label className="text-base font-medium mb-3 block">
                  Quality: {quality[0]}%
                </Label>
                <Slider
                  value={quality}
                  onValueChange={setQuality}
                  max={100}
                  min={10}
                  step={5}
                  className="w-full"
                  data-testid="slider-quality"
                />
                <div className="flex justify-between text-xs text-muted-foreground mt-2">
                  <span>Smallest Size</span>
                  <span>Best Quality</span>
                </div>
              </div>

              <div>
                <Label htmlFor="output-format" className="text-base font-medium">
                  Output Format
                </Label>
                <Select value={format} onValueChange={setFormat}>
                  <SelectTrigger className="mt-2" data-testid="select-format">
                    <SelectValue placeholder="Select output format" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="original">Keep Original Format</SelectItem>
                    <SelectItem value="jpeg">JPEG (Best for photos)</SelectItem>
                    <SelectItem value="png">PNG (Best for graphics)</SelectItem>
                    <SelectItem value="webp">WebP (Modern, smaller size)</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              {/* Resize Options */}
              <div className="space-y-4">
                <div className="flex items-center space-x-2">
                  <Switch
                    id="resize-enabled"
                    checked={resizeEnabled}
                    onCheckedChange={setResizeEnabled}
                    data-testid="switch-resize"
                  />
                  <Label htmlFor="resize-enabled" className="text-base font-medium">
                    Resize Image
                  </Label>
                </div>

                {resizeEnabled && (
                  <div className="grid grid-cols-2 gap-4 ml-6">
                    <div>
                      <Label className="text-sm font-medium mb-2 block">
                        Max Width: {maxWidth[0]}px
                      </Label>
                      <Slider
                        value={maxWidth}
                        onValueChange={setMaxWidth}
                        max={4000}
                        min={100}
                        step={50}
                        data-testid="slider-width"
                      />
                    </div>
                    <div>
                      <Label className="text-sm font-medium mb-2 block">
                        Max Height: {maxHeight[0]}px
                      </Label>
                      <Slider
                        value={maxHeight}
                        onValueChange={setMaxHeight}
                        max={4000}
                        min={100}
                        step={50}
                        data-testid="slider-height"
                      />
                    </div>
                  </div>
                )}
              </div>

              {/* Expected Results */}
              <div className="bg-primary/5 border border-primary/20 rounded-lg p-4">
                <h5 className="font-medium mb-2">Expected Results:</h5>
                <ul className="text-sm text-muted-foreground space-y-1">
                  <li>â€¢ File size reduction: ~{getExpectedReduction()}%</li>
                  <li>â€¢ Image quality: {quality[0]}% of original</li>
                  <li>â€¢ Format: {format === 'original' ? 'Unchanged' : format.toUpperCase()}</li>
                  {resizeEnabled && <li>â€¢ Dimensions: Max {maxWidth[0]}Ã—{maxHeight[0]}px</li>}
                </ul>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Tool Features */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-muted-foreground">
          <div>
            <h4 className="font-medium text-foreground mb-2">Lossless & Lossy Options</h4>
            <p>Choose between lossless compression for perfect quality or lossy for maximum size reduction.</p>
          </div>
          <div>
            <h4 className="font-medium text-foreground mb-2">Format Conversion</h4>
            <p>Convert images to modern formats like WebP for better compression and web performance.</p>
          </div>
          <div>
            <h4 className="font-medium text-foreground mb-2">Smart Resizing</h4>
            <p>Automatically resize images while maintaining aspect ratio for consistent dimensions.</p>
          </div>
          <div>
            <h4 className="font-medium text-foreground mb-2">Batch Processing</h4>
            <p>Compress multiple images with the same settings for efficient workflow management.</p>
          </div>
        </div>
      </div>
    </UniversalToolInterface>
  );
}

client/src/tools/image/ImageResizer.tsx.content:
import { useEffect, useState } from "react";
import UniversalToolInterface from "@/components/UniversalToolInterface";
import { Card, CardContent } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Button } from "@/components/ui/button";
import { Square, RotateCcw } from "lucide-react";

export default function ImageResizer() {
  const [width, setWidth] = useState("800");
  const [height, setHeight] = useState("600");
  const [maintainAspectRatio, setMaintainAspectRatio] = useState(true);
  const [resizeMode, setResizeMode] = useState("percentage");
  const [percentage, setPercentage] = useState("100");
  const [unit, setUnit] = useState("px");
  const [originalDimensions, setOriginalDimensions] = useState<{width: number, height: number} | null>(null);

  useEffect(() => {
    document.title = "Image Resizer - ToolSuite Pro | Resize Images Online";

    const metaDescription = document.querySelector('meta[name="description"]');
    if (metaDescription) {
      metaDescription.setAttribute('content', 'Resize images online with custom dimensions, percentage scaling, and aspect ratio controls. Support for multiple formats and batch processing.');
    }
  }, []);

  const toolConfig = {
    name: "Image Resizer",
    description: "Resize images to specific dimensions or scale by percentage. Maintain aspect ratio or stretch to custom sizes with professional quality algorithms.",
    acceptedFormats: [".jpg", ".jpeg", ".png", ".webp", ".bmp", ".gif"],
    outputFormat: "Same as input",
    maxFileSize: 50, // 50MB
  };

  const handleProcess = async (file: File): Promise<{ success: boolean; downloadUrl?: string; error?: string }> => {
    // Get original image dimensions
    const img = new Image();
    const imageLoaded = new Promise<void>((resolve, reject) => {
      img.onload = () => {
        setOriginalDimensions({ width: img.width, height: img.height });
        resolve();
      };
      img.onerror = reject;
    });

    img.src = URL.createObjectURL(file);

    try {
      await imageLoaded;
    } catch {
      return {
        success: false,
        error: "Failed to load image. The file may be corrupted."
      };
    }

    // Calculate target dimensions
    let targetWidth: number;
    let targetHeight: number;

    if (resizeMode === "percentage") {
      const scale = parseFloat(percentage) / 100;
      targetWidth = Math.round(img.width * scale);
      targetHeight = Math.round(img.height * scale);
    } else {
      targetWidth = parseInt(width);
      targetHeight = parseInt(height);
    }

    // Simulate processing time
    await new Promise(resolve => setTimeout(resolve, 2000));

    // Create canvas and resize image
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    canvas.width = targetWidth;
    canvas.height = targetHeight;

    // Draw resized image
    ctx!.drawImage(img, 0, 0, targetWidth, targetHeight);

    // Add watermark to show this is resized
    ctx!.fillStyle = 'rgba(255, 255, 255, 0.1)';
    ctx!.font = '16px Arial';
    ctx!.fillText(`${targetWidth}Ã—${targetHeight}`, 10, 30);

    // Convert to blob and create download URL
    return new Promise((resolve) => {
      canvas.toBlob((blob) => {
        if (blob) {
          const downloadUrl = URL.createObjectURL(blob);
          resolve({
            success: true,
            downloadUrl: downloadUrl
          });
        } else {
          resolve({
            success: false,
            error: "Failed to resize image"
          });
        }
      }, file.type, 0.9);
    });
  };

  const handleAspectRatioToggle = (enabled: boolean) => {
    setMaintainAspectRatio(enabled);
    if (enabled && originalDimensions) {
      // Recalculate height based on width and aspect ratio
      const aspectRatio = originalDimensions.width / originalDimensions.height;
      const newHeight = Math.round(parseInt(width) / aspectRatio);
      setHeight(newHeight.toString());
    }
  };

  const handleWidthChange = (value: string) => {
    setWidth(value);
    if (maintainAspectRatio && originalDimensions) {
      const aspectRatio = originalDimensions.width / originalDimensions.height;
      const newHeight = Math.round(parseInt(value) / aspectRatio);
      setHeight(newHeight.toString());
    }
  };

  const handleHeightChange = (value: string) => {
    setHeight(value);
    if (maintainAspectRatio && originalDimensions) {
      const aspectRatio = originalDimensions.width / originalDimensions.height;
      const newWidth = Math.round(parseInt(value) * aspectRatio);
      setWidth(newWidth.toString());
    }
  };

  const resetToOriginal = () => {
    if (originalDimensions) {
      setWidth(originalDimensions.width.toString());
      setHeight(originalDimensions.height.toString());
      setPercentage("100");
    }
  };

  return (
    <UniversalToolInterface config={toolConfig} onProcess={handleProcess}>
      <div className="mt-6 space-y-6">
        {/* Resize Mode Selection */}
        <Card className="glassmorphism">
          <CardContent className="p-6">
            <h4 className="font-semibold mb-4">Resize Settings</h4>

            <div className="space-y-6">
              <div>
                <Label htmlFor="resize-mode" className="text-base font-medium">
                  Resize Mode
                </Label>
                <Select value={resizeMode} onValueChange={setResizeMode}>
                  <SelectTrigger className="mt-2" data-testid="select-resize-mode">
                    <SelectValue placeholder="Select resize mode" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="percentage">By Percentage</SelectItem>
                    <SelectItem value="dimensions">Custom Dimensions</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              {/* Percentage Mode */}
              {resizeMode === "percentage" && (
                <div>
                  <Label htmlFor="percentage" className="text-base font-medium">
                    Scale Percentage
                  </Label>
                  <div className="flex items-center space-x-2 mt-2">
                    <Input
                      id="percentage"
                      type="number"
                      min="1"
                      max="500"
                      value={percentage}
                      onChange={(e) => setPercentage(e.target.value)}
                      className="flex-1"
                      data-testid="input-percentage"
                    />
                    <span className="text-muted-foreground">%</span>
                  </div>
                  <p className="text-sm text-muted-foreground mt-2">
                    100% = Original size, 50% = Half size, 200% = Double size
                  </p>
                </div>
              )}

              {/* Dimensions Mode */}
              {resizeMode === "dimensions" && (
                <div className="space-y-4">
                  <div className="flex items-center space-x-2">
                    <Switch
                      id="aspect-ratio"
                      checked={maintainAspectRatio}
                      onCheckedChange={handleAspectRatioToggle}
                      data-testid="switch-aspect-ratio"
                    />
                    <Label htmlFor="aspect-ratio" className="text-base font-medium">
                      Maintain Aspect Ratio
                    </Label>
                    <Square className="ml-2 text-muted-foreground" size={16} />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="width" className="text-sm font-medium">
                        Width
                      </Label>
                      <div className="flex items-center space-x-2 mt-1">
                        <Input
                          id="width"
                          type="number"
                          min="1"
                          max="10000"
                          value={width}
                          onChange={(e) => handleWidthChange(e.target.value)}
                          data-testid="input-width"
                        />
                        <span className="text-muted-foreground text-sm">{unit}</span>
                      </div>
                    </div>
                    <div>
                      <Label htmlFor="height" className="text-sm font-medium">
                        Height
                      </Label>
                      <div className="flex items-center space-x-2 mt-1">
                        <Input
                          id="height"
                          type="number"
                          min="1"
                          max="10000"
                          value={height}
                          onChange={(e) => handleHeightChange(e.target.value)}
                          disabled={maintainAspectRatio}
                          data-testid="input-height"
                        />
                        <span className="text-muted-foreground text-sm">{unit}</span>
                      </div>
                    </div>
                  </div>

                  {originalDimensions && (
                    <div className="flex items-center justify-between p-3 bg-muted/20 rounded-lg">
                      <div className="text-sm">
                        <span className="text-muted-foreground">Original: </span>
                        <span className="font-medium">
                          {originalDimensions.width} Ã— {originalDimensions.height} px
                        </span>
                      </div>
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={resetToOriginal}
                        data-testid="button-reset-original"
                      >
                        <RotateCcw className="mr-2" size={14} />
                        Reset
                      </Button>
                    </div>
                  )}
                </div>
              )}

              {/* Preview */}
              <div className="bg-primary/5 border border-primary/20 rounded-lg p-4">
                <h5 className="font-medium mb-2">Preview Settings:</h5>
                <ul className="text-sm text-muted-foreground space-y-1">
                  {resizeMode === "percentage" ? (
                    <li>â€¢ Scale: {percentage}% of original size</li>
                  ) : (
                    <>
                      <li>â€¢ Target size: {width} Ã— {height} px</li>
                      <li>â€¢ Aspect ratio: {maintainAspectRatio ? 'Maintained' : 'Custom'}</li>
                    </>
                  )}
                  <li>â€¢ Quality: High (lossless scaling algorithm)</li>
                </ul>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Common Presets */}
        <Card className="glassmorphism">
          <CardContent className="p-6">
            <h4 className="font-semibold mb-4">Quick Presets</h4>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
              {[
                { name: "HD", width: "1920", height: "1080" },
                { name: "Square 1K", width: "1000", height: "1000" },
                { name: "Instagram", width: "1080", height: "1080" },
                { name: "Facebook Cover", width: "1200", height: "630" },
                { name: "Twitter Header", width: "1500", height: "500" },
                { name: "YouTube Thumb", width: "1280", height: "720" },
                { name: "LinkedIn Post", width: "1200", height: "627" },
                { name: "Story (9:16)", width: "1080", height: "1920" },
              ].map((preset, index) => (
                <Button
                  key={index}
                  variant="outline"
                  size="sm"
                  onClick={() => {
                    setResizeMode("dimensions");
                    setWidth(preset.width);
                    setHeight(preset.height);
                    setMaintainAspectRatio(false);
                  }}
                  className="text-xs"
                  data-testid={`preset-${preset.name.toLowerCase().replace(/\s+/g, '-')}`}
                >
                  {preset.name}
                </Button>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Tool Features */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-muted-foreground">
          <div>
            <h4 className="font-medium text-foreground mb-2">Smart Scaling</h4>
            <p>Advanced algorithms preserve image quality during resize operations, minimizing pixelation.</p>
          </div>
          <div>
            <h4 className="font-medium text-foreground mb-2">Aspect Ratio Control</h4>
            <p>Maintain original proportions or create custom dimensions for specific requirements.</p>
          </div>
          <div>
            <h4 className="font-medium text-foreground mb-2">Social Media Presets</h4>
            <p>Quick presets for popular social media platforms and common image sizes.</p>
          </div>
