                    </Button>

                    {/* Numbers and operations */}
                    {[
                      ["7", "8", "9", "Ã—"],
                      ["4", "5", "6", "-"],
                      ["1", "2", "3", "+"],
                      ["0", ".", "=", "="]
                    ].map((row, i) => (
                      row.map((btn, j) => {
                        if (btn === "=") {
                          return j === 2 ? (
