                        return (
                          <Button
                            key={`${i}-${j}`}
                            variant="outline"
                            onClick={() => {
                              if (btn === ".") {
                                inputDecimal();
                              } else if (isNumber) {
                                inputNumber(btn);
                              } else if (isOperation) {
                                inputOperation(btn);
                              }
                            }}
                            className={isOperation ? "gradient-bg" : ""}
                            data-testid={`button-${btn === "." ? "decimal" : btn}`}
                          >
