                  ) : (
                    <div className="space-y-2" data-testid="calculation-history">
                      {history.map((calc, index) => (
                        <div
                          key={index}
                          className="p-2 bg-muted/20 rounded text-sm font-mono cursor-pointer hover:bg-muted/30"
                          onClick={() => {
                            const result = calc.split("= ")[1];
