                    </div>
                    <div>
                      <Label htmlFor="email-body">Message</Label>
                      <Textarea
                        id="email-body"
                        value={formData.emailBody}
                        onChange={(e) => setFormData({...formData, emailBody: e.target.value})}
                        placeholder="Email message..."
                        className="mt-2"
                        data-testid="textarea-email-body"
                      />
                    </div>
                  </div>
