                      />
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <Label htmlFor="event-start">Start Date & Time</Label>
                        <Input
                          id="event-start"
                          type="datetime-local"
                          value={formData.eventStart}
                          onChange={(e) => setFormData({...formData, eventStart: e.target.value})}
                          className="mt-2"
                          data-testid="input-event-start"
                        />
                      </div>
                      <div>
                        <Label htmlFor="event-end">End Date & Time</Label>
                        <Input
                          id="event-end"
                          type="datetime-local"
                          value={formData.eventEnd}
                          onChange={(e) => setFormData({...formData, eventEnd: e.target.value})}
                          className="mt-2"
                          data-testid="input-event-end"
                        />
                      </div>
                    </div>
                  </div>
                </TabsContent>
              </Tabs>

              {/* Size Settings */}
              <div>
                <Label className="text-base font-medium mb-3 block">
                  Size: {size[0]}px
                </Label>
                <Slider
                  value={size}
                  onValueChange={setSize}
                  max={1000}
                  min={100}
                  step={50}
                  className="w-full"
                  data-testid="slider-size"
                />
                <div className="flex justify-between mt-2">
                  {presetSizes.map((preset) => (
                    <Button
                      key={preset.name}
                      variant="outline"
                      size="sm"
                      onClick={() => setSize([preset.size])}
                      className="text-xs"
                      data-testid={`preset-${preset.name.toLowerCase()}`}
                    >
                      {preset.name}
                    </Button>
                  ))}
                </div>
              </div>

              {/* Color Settings */}
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="foreground-color">Foreground Color</Label>
                  <div className="flex items-center space-x-2 mt-2">
                    <input
                      id="foreground-color"
                      type="color"
                      value={foregroundColor}
                      onChange={(e) => setForegroundColor(e.target.value)}
                      className="w-12 h-10 rounded border border-border cursor-pointer"
                      data-testid="color-foreground"
                    />
                    <Input
                      value={foregroundColor}
                      onChange={(e) => setForegroundColor(e.target.value)}
                      className="flex-1"
                    />
                  </div>
                </div>
                <div>
                  <Label htmlFor="background-color">Background Color</Label>
                  <div className="flex items-center space-x-2 mt-2">
                    <input
                      id="background-color"
                      type="color"
                      value={backgroundColor}
                      onChange={(e) => setBackgroundColor(e.target.value)}
                      className="w-12 h-10 rounded border border-border cursor-pointer"
                      data-testid="color-background"
                    />
                    <Input
                      value={backgroundColor}
                      onChange={(e) => setBackgroundColor(e.target.value)}
                      className="flex-1"
                    />
                  </div>
                </div>
              </div>

              {/* Error Correction */}
              <div>
                <Label htmlFor="error-correction">Error Correction Level</Label>
                <Select value={errorCorrection} onValueChange={setErrorCorrection}>
                  <SelectTrigger className="mt-2" data-testid="select-error-correction">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="L">Low (7%)</SelectItem>
                    <SelectItem value="M">Medium (15%)</SelectItem>
                    <SelectItem value="Q">Quartile (25%)</SelectItem>
                    <SelectItem value="H">High (30%)</SelectItem>
                  </SelectContent>
                </Select>
                <p className="text-xs text-muted-foreground mt-1">
                  Higher levels can recover from more damage but create larger QR codes
                </p>
              </div>

              {/* Generate Button */}
              <Button
                onClick={generateQRCode}
                disabled={isGenerating}
                className="w-full gradient-bg"
                data-testid="button-generate"
              >
                {isGenerating ? (
                  <>
                    <RefreshCw className="mr-2 animate-spin" size={16} />
                    Generating...
                  </>
                ) : (
                  <>
                    <QrCode className="mr-2" size={16} />
                    Generate QR Code
                  </>
                )}
              </Button>
            </CardContent>
          </Card>
        </div>

        {/* QR Code Display */}
        <div className="space-y-6">
          <Card className="glassmorphism">
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle>Generated QR Code</CardTitle>
                {qrCodeDataUrl && (
                  <div className="flex space-x-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={copyQRCode}
                      data-testid="button-copy-qr"
                    >
                      <Copy size={16} className="mr-2" />
                      Copy
                    </Button>
                    <Button
                      size="sm"
                      onClick={downloadQRCode}
                      className="gradient-bg"
                      data-testid="button-download-qr"
                    >
                      <Download size={16} className="mr-2" />
