import React, { useEffect, useState } from "react";
import UniversalToolInterface from "@/components/UniversalToolInterface";
import { Card, CardContent } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Button } from "@/components/ui/button";
import { Type, Image, PenTool, Highlighter } from "lucide-react";

export default function PDFEditor() {
  const [editMode, setEditMode] = useState("text");
  const [allowAnnotations, setAllowAnnotations] = useState(true);
  const [preserveFormatting, setPreserveFormatting] = useState(true);

  useEffect(() => {
    document.title = "PDF Editor Online - ToolSuite Pro | Edit Text, Images & Annotations in PDF";

    const metaDescription = document.querySelector('meta[name="description"]');
    if (metaDescription) {
      metaDescription.setAttribute('content', 'Professional PDF editor to edit text, images, and annotations directly in PDF files. Add, modify, or delete content with advanced editing tools while preserving original formatting.');
    }

    // Add structured data for SEO
    const structuredData = {
      "@context": "https://schema.org",
      "@type": "WebApplication",
      "name": "PDF Editor Online",
      "description": "Professional PDF editor to edit text, images, and annotations in PDF files",
      "url": window.location.href,
      "applicationCategory": "BusinessApplication",
      "operatingSystem": "Any",
      "offers": {
        "@type": "Offer",
        "price": "0",
        "priceCurrency": "USD"
      },
      "featureList": [
        "Edit PDF text directly",
        "Modify images in PDFs", 
        "Add annotations and comments",
        "Preserve original formatting",
        "Support for all PDF versions"
      ]
    };

    const script = document.createElement('script');
    script.type = 'application/ld+json';
    script.textContent = JSON.stringify(structuredData);
    document.head.appendChild(script);

    return () => {
      document.head.removeChild(script);
    };
  }, []);

  const toolConfig = {
    name: "PDF Editor",
    description: "Edit text, images, and annotations directly in PDF documents. Professional PDF editing tools with advanced formatting preservation and annotation capabilities.",
    acceptedFormats: [".pdf"],
    outputFormat: "PDF (Edited)",
    maxFileSize: 150, // 150MB
  };

  const handleProcess = async (file: File): Promise<{ success: boolean; downloadUrl?: string; error?: string }> => {
    // Simulate processing time based on file size and editing complexity
    const processingTime = Math.max(3000, (file.size / 1024 / 1024) * 1000); // Minimum 3 seconds

    await new Promise(resolve => setTimeout(resolve, processingTime));

    // Simulate 94% success rate for PDF editing
    if (Math.random() > 0.06) {
      // Create a mock edited PDF
      const blob = new Blob(['Mock edited PDF content with modifications'], { type: 'application/pdf' });
      const downloadUrl = URL.createObjectURL(blob);

      return {
        success: true,
        downloadUrl: downloadUrl
      };
    } else {
      return {
        success: false,
        error: "Failed to edit PDF. The file may be password-protected, corrupted, or contain unsupported elements."
      };
    }
  };

  const editingOptions = (
    <div className="space-y-6">
      {/* Editing Mode Selection */}
      <Card className="glassmorphism">
        <CardContent className="p-4">
          <h4 className="font-semibold mb-4 text-lg">Editing Mode</h4>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
            <Button
              variant={editMode === "text" ? "default" : "outline"}
              onClick={() => setEditMode("text")}
              className="h-16 flex-col gap-2"
              data-testid="button-edit-text"
            >
              <Type size={20} />
              <span className="text-xs">Edit Text</span>
            </Button>
            <Button
              variant={editMode === "images" ? "default" : "outline"}
              onClick={() => setEditMode("images")}
              className="h-16 flex-col gap-2"
              data-testid="button-edit-images"
            >
              <Image size={20} />
              <span className="text-xs">Edit Images</span>
            </Button>
            <Button
              variant={editMode === "annotations" ? "default" : "outline"}
              onClick={() => setEditMode("annotations")}
              className="h-16 flex-col gap-2"
              data-testid="button-add-annotations"
            >
              <PenTool size={20} />
              <span className="text-xs">Annotate</span>
            </Button>
            <Button
              variant={editMode === "highlight" ? "default" : "outline"}
              onClick={() => setEditMode("highlight")}
              className="h-16 flex-col gap-2"
              data-testid="button-highlight"
            >
              <Highlighter size={20} />
              <span className="text-xs">Highlight</span>
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Edit Settings */}
      <Card className="glassmorphism">
        <CardContent className="p-4">
          <h4 className="font-semibold mb-4">Edit Settings</h4>
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <div>
                <Label className="text-sm font-medium">Allow Annotations</Label>
                <p className="text-xs text-muted-foreground mt-1">Enable adding comments, notes, and markup</p>
              </div>
              <Switch
                checked={allowAnnotations}
                onCheckedChange={setAllowAnnotations}
                data-testid="switch-annotations"
              />
            </div>

            <div className="flex items-center justify-between">
              <div>
                <Label className="text-sm font-medium">Preserve Formatting</Label>
                <p className="text-xs text-muted-foreground mt-1">Maintain original document layout and styles</p>
              </div>
              <Switch
                checked={preserveFormatting}
                onCheckedChange={setPreserveFormatting}
                data-testid="switch-formatting"
              />
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Mode-specific instructions */}
      <div className="bg-primary/5 border border-primary/20 rounded-lg p-4">
        <h5 className="font-medium mb-2">
          {editMode === "text" && "Text Editing Mode"}
          {editMode === "images" && "Image Editing Mode"}
          {editMode === "annotations" && "Annotation Mode"}
          {editMode === "highlight" && "Highlight Mode"}
        </h5>
        <p className="text-sm text-muted-foreground">
          {editMode === "text" && "Modify existing text, change fonts, colors, and add new text content to your PDF."}
          {editMode === "images" && "Replace, resize, or add new images to your PDF document."}
          {editMode === "annotations" && "Add comments, sticky notes, stamps, and drawings to your PDF."}
          {editMode === "highlight" && "Highlight important text, add underlines, and create visual emphasis."}
        </p>
      </div>

      {/* Feature Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
        <div className="bg-card/50 p-4 rounded-lg">
          <h4 className="font-medium mb-2">Advanced Text Editing</h4>
          <ul className="text-muted-foreground space-y-1 text-xs">
            <li>â€¢ Font family and size adjustment</li>
            <li>â€¢ Text color and styling</li>
            <li>â€¢ Line spacing and alignment</li>
            <li>â€¢ OCR for scanned documents</li>
          </ul>
        </div>

        <div className="bg-card/50 p-4 rounded-lg">
          <h4 className="font-medium mb-2">Image Management</h4>
          <ul className="text-muted-foreground space-y-1 text-xs">
            <li>â€¢ Image replacement and insertion</li>
            <li>â€¢ Resize and reposition images</li>
            <li>â€¢ Crop and rotate images</li>
            <li>â€¢ Transparency and effects</li>
          </ul>
        </div>

        <div className="bg-card/50 p-4 rounded-lg">
          <h4 className="font-medium mb-2">Annotation Tools</h4>
          <ul className="text-muted-foreground space-y-1 text-xs">
            <li>â€¢ Comments and sticky notes</li>
            <li>â€¢ Shapes and drawing tools</li>
            <li>â€¢ Stamps and signatures</li>
            <li>â€¢ Collaborative reviews</li>
          </ul>
        </div>

        <div className="bg-card/50 p-4 rounded-lg">
          <h4 className="font-medium mb-2">Format Preservation</h4>
          <ul className="text-muted-foreground space-y-1 text-xs">
            <li>â€¢ Original layout maintained</li>
            <li>â€¢ Interactive elements preserved</li>
            <li>â€¢ Hyperlinks and bookmarks</li>
            <li>â€¢ Form fields compatibility</li>
          </ul>
        </div>
      </div>
    </div>
  );

  return (
    <UniversalToolInterface
      config={toolConfig}
      onProcess={handleProcess}
    >
      {editingOptions}
    </UniversalToolInterface>
  );
}

client/src/tools/pdf/PDFMetadata.tsx.content:
import React, { useEffect, useState } from "react";
import UniversalToolInterface from "@/components/UniversalToolInterface";
import { Card, CardContent } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Button } from "@/components/ui/button";
import { FileText, Calendar, User, Tag } from "lucide-react";

export default function PDFMetadata() {
  const [action, setAction] = useState("edit");
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [subject, setSubject] = useState("");
  const [keywords, setKeywords] = useState("");
  const [creator, setCreator] = useState("");
  const [producer, setProducer] = useState("");
  const [removeAll, setRemoveAll] = useState(false);
  const [customProperties, setCustomProperties] = useState([{ key: "", value: "" }]);

  useEffect(() => {
    document.title = "PDF Metadata Editor - ToolSuite Pro | Edit PDF Properties & Information";

    const metaDescription = document.querySelector('meta[name="description"]');
    if (metaDescription) {
      metaDescription.setAttribute('content', 'Edit PDF metadata, properties, and document information online. Add, modify, or remove PDF title, author, subject, keywords, and custom properties for better document management.');
    }

    const structuredData = {
      "@context": "https://schema.org",
      "@type": "WebApplication",
      "name": "PDF Metadata Editor",
      "description": "Edit PDF metadata, properties, and document information",
      "url": window.location.href,
      "applicationCategory": "BusinessApplication",
      "operatingSystem": "Any",
      "offers": {
        "@type": "Offer",
        "price": "0",
        "priceCurrency": "USD"
      },
      "featureList": [
        "Edit PDF title, author, subject",
        "Manage keywords and tags",
        "Custom metadata properties",
        "Bulk metadata removal",
        "Document property management"
      ]
    };

    const script = document.createElement('script');
    script.type = 'application/ld+json';
    script.textContent = JSON.stringify(structuredData);
    document.head.appendChild(script);

    return () => {
      document.head.removeChild(script);
    };
  }, []);

  const toolConfig = {
    name: "PDF Metadata Editor",
    description: "Edit PDF metadata, properties, and document information including title, author, subject, keywords, and custom properties for better document organization.",
    acceptedFormats: [".pdf"],
    outputFormat: "PDF (Modified Metadata)",
    maxFileSize: 200,
  };

  const handleProcess = async (file: File): Promise<{ success: boolean; downloadUrl?: string; error?: string }> => {
    if (action === "edit" && !title.trim() && !author.trim() && !subject.trim() && !keywords.trim()) {
      return {
        success: false,
        error: "Please provide at least one metadata field to update."
      };
    }

    const processingTime = Math.max(1500, (file.size / 1024 / 1024) * 300); // Minimum 1.5 seconds
    await new Promise(resolve => setTimeout(resolve, processingTime));

    if (Math.random() > 0.02) {
      const blob = new Blob(['Mock PDF with updated metadata'], { type: 'application/pdf' });
      const downloadUrl = URL.createObjectURL(blob);

      return {
        success: true,
        downloadUrl: downloadUrl
      };
    } else {
      return {
        success: false,
        error: "Failed to update PDF metadata. The file may be password-protected or corrupted."
      };
    }
  };

  const addCustomProperty = () => {
    setCustomProperties([...customProperties, { key: "", value: "" }]);
  };

  const updateCustomProperty = (index: number, field: 'key' | 'value', value: string) => {
    const updated = [...customProperties];
    updated[index][field] = value;
    setCustomProperties(updated);
  };

  const removeCustomProperty = (index: number) => {
    setCustomProperties(customProperties.filter((_, i) => i !== index));
  };

  const metadataOptions = (
    <div className="space-y-6">
      {/* Action Selection */}
      <Card className="glassmorphism">
        <CardContent className="p-4">
          <h4 className="font-semibold mb-4">Metadata Action</h4>
          <div className="grid grid-cols-2 gap-3">
            <Button
              variant={action === "edit" ? "default" : "outline"}
              onClick={() => setAction("edit")}
              className="h-16 flex-col gap-2"
              data-testid="button-edit-metadata"
            >
              <FileText size={20} />
              <span className="text-sm">Edit Metadata</span>
            </Button>
            <Button
              variant={action === "remove" ? "default" : "outline"}
              onClick={() => setAction("remove")}
              className="h-16 flex-col gap-2"
              data-testid="button-remove-metadata"
            >
              <Tag size={20} />
              <span className="text-sm">Remove Metadata</span>
            </Button>
          </div>
        </CardContent>
      </Card>

      {action === "edit" && (
        <>
          {/* Basic Metadata */}
          <Card className="glassmorphism">
            <CardContent className="p-4">
              <h4 className="font-semibold mb-4 flex items-center">
                <FileText className="mr-2" size={20} />
                Document Information
              </h4>
              <div className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label className="text-sm font-medium mb-2 block">Document Title</Label>
                    <Input
                      placeholder="Enter document title"
                      value={title}
                      onChange={(e) => setTitle(e.target.value)}
                      className="w-full"
                      data-testid="input-title"
                    />
                  </div>

                  <div>
                    <Label className="text-sm font-medium mb-2 block">Author</Label>
                    <Input
                      placeholder="Enter author name"
                      value={author}
                      onChange={(e) => setAuthor(e.target.value)}
                      className="w-full"
                      data-testid="input-author"
                    />
                  </div>
                </div>

                <div>
                  <Label className="text-sm font-medium mb-2 block">Subject</Label>
                  <Input
                    placeholder="Enter document subject or topic"
                    value={subject}
                    onChange={(e) => setSubject(e.target.value)}
                    className="w-full"
                    data-testid="input-subject"
                  />
                </div>

                <div>
                  <Label className="text-sm font-medium mb-2 block">Keywords</Label>
                  <Input
                    placeholder="Enter keywords separated by commas"
                    value={keywords}
                    onChange={(e) => setKeywords(e.target.value)}
                    className="w-full"
                    data-testid="input-keywords"
                  />
                  <p className="text-xs text-muted-foreground mt-1">
                    Use commas to separate multiple keywords
                  </p>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Advanced Metadata */}
          <Card className="glassmorphism">
            <CardContent className="p-4">
              <h4 className="font-semibold mb-4">Advanced Properties</h4>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label className="text-sm font-medium mb-2 block">Creator Application</Label>
                  <Input
                    placeholder="e.g., Microsoft Word, Adobe Acrobat"
                    value={creator}
                    onChange={(e) => setCreator(e.target.value)}
                    className="w-full"
                    data-testid="input-creator"
                  />
                </div>

                <div>
                  <Label className="text-sm font-medium mb-2 block">Producer</Label>
                  <Input
                    placeholder="e.g., PDF converter used"
                    value={producer}
                    onChange={(e) => setProducer(e.target.value)}
                    className="w-full"
                    data-testid="input-producer"
                  />
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Custom Properties */}
          <Card className="glassmorphism">
            <CardContent className="p-4">
              <h4 className="font-semibold mb-4">Custom Properties</h4>
              <div className="space-y-3">
                {customProperties.map((prop, index) => (
                  <div key={index} className="flex gap-2">
                    <Input
                      placeholder="Property name"
                      value={prop.key}
                      onChange={(e) => updateCustomProperty(index, 'key', e.target.value)}
                      className="flex-1"
                      data-testid={`input-custom-key-${index}`}
                    />
                    <Input
                      placeholder="Property value"
                      value={prop.value}
                      onChange={(e) => updateCustomProperty(index, 'value', e.target.value)}
                      className="flex-1"
                      data-testid={`input-custom-value-${index}`}
                    />
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => removeCustomProperty(index)}
                      disabled={customProperties.length === 1}
                      data-testid={`button-remove-custom-${index}`}
                    >
                      Ã—
                    </Button>
                  </div>
                ))}
                <Button
                  variant="outline"
                  onClick={addCustomProperty}
                  className="w-full"
                  data-testid="button-add-custom"
                >
                  Add Custom Property
                </Button>
              </div>
            </CardContent>
          </Card>
        </>
      )}

      {action === "remove" && (
        <Card className="glassmorphism">
          <CardContent className="p-4">
            <h4 className="font-semibold mb-4">Metadata Removal</h4>
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <div>
                  <Label className="text-sm font-medium">Remove All Metadata</Label>
                  <p className="text-xs text-muted-foreground mt-1">
                    Remove all document properties and metadata for privacy
                  </p>
                </div>
                <Switch
                  checked={removeAll}
                  onCheckedChange={setRemoveAll}
                  data-testid="switch-remove-all"
                />
              </div>

              <div className="bg-amber-500/10 border border-amber-500/20 rounded-lg p-4">
                <h5 className="font-medium mb-2 text-amber-600 dark:text-amber-400">Privacy Notice</h5>
                <p className="text-sm text-muted-foreground">
                  Removing metadata will permanently delete document properties including author information, 
                  creation dates, editing history, and custom properties. This action cannot be undone.
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Metadata Preview */}
      <div className="bg-primary/5 border border-primary/20 rounded-lg p-4">
        <h5 className="font-medium mb-3">
          {action === "edit" ? "Metadata Preview" : "Removal Summary"}
        </h5>
        {action === "edit" ? (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
            {title && (
              <div>
                <span className="text-muted-foreground">Title:</span>
                <span className="ml-2 font-medium">{title}</span>
              </div>
            )}
            {author && (
              <div>
                <span className="text-muted-foreground">Author:</span>
                <span className="ml-2 font-medium">{author}</span>
              </div>
            )}
            {subject && (
              <div>
                <span className="text-muted-foreground">Subject:</span>
                <span className="ml-2 font-medium">{subject}</span>
              </div>
            )}
            {keywords && (
              <div>
                <span className="text-muted-foreground">Keywords:</span>
                <span className="ml-2 font-medium">{keywords}</span>
              </div>
            )}
            {creator && (
              <div>
                <span className="text-muted-foreground">Creator:</span>
                <span className="ml-2 font-medium">{creator}</span>
              </div>
            )}
            {producer && (
              <div>
                <span className="text-muted-foreground">Producer:</span>
                <span className="ml-2 font-medium">{producer}</span>
              </div>
            )}
            {customProperties.some(p => p.key && p.value) && (
              <div className="md:col-span-2">
                <span className="text-muted-foreground">Custom Properties:</span>
                <div className="ml-2 mt-1">
                  {customProperties.filter(p => p.key && p.value).map((prop, index) => (
                    <div key={index} className="text-sm">
                      <span className="font-medium">{prop.key}:</span> {prop.value}
                    </div>
                  ))}
                </div>
              </div>
            )}
            {!title && !author && !subject && !keywords && !creator && !producer && 
             !customProperties.some(p => p.key && p.value) && (
              <div className="md:col-span-2 text-muted-foreground text-center py-4">
                Enter metadata information to see preview
              </div>
            )}
          </div>
