import React, { useEffect, useState } from "react";
import UniversalToolInterface from "@/components/UniversalToolInterface";
import { Card, CardContent } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { AlertTriangle, Wrench, CheckCircle2 } from "lucide-react";

export default function PDFRepair() {
  const [repairMode, setRepairMode] = useState("auto");
  const [recoverImages, setRecoverImages] = useState(true);
  const [recoverText, setRecoverText] = useState(true);
  const [rebuildIndex, setRebuildIndex] = useState(true);
  const [removeCorruption, setRemoveCorruption] = useState(true);

  useEffect(() => {
    document.title = "PDF Repair Tool - ToolSuite Pro | Fix Corrupted & Damaged PDF Files";

    const metaDescription = document.querySelector('meta[name="description"]');
    if (metaDescription) {
      metaDescription.setAttribute('content', 'Repair corrupted and damaged PDF files online. Fix PDF errors, recover content, rebuild document structure, and restore accessibility to unreadable PDF documents.');
    }

    const structuredData = {
      "@context": "https://schema.org",
      "@type": "WebApplication",
      "name": "PDF Repair Tool",
      "description": "Repair corrupted and damaged PDF files with advanced recovery algorithms",
      "url": window.location.href,
      "applicationCategory": "UtilitiesApplication",
      "operatingSystem": "Any",
      "offers": {
        "@type": "Offer",
        "price": "0",
        "priceCurrency": "USD"
      },
      "featureList": [
        "Corrupted PDF file repair",
        "Content recovery algorithms",
        "Document structure rebuilding",
        "Image and text recovery",
        "Multiple repair strategies"
      ]
    };

    const script = document.createElement('script');
    script.type = 'application/ld+json';
    script.textContent = JSON.stringify(structuredData);
    document.head.appendChild(script);

    return () => {
      document.head.removeChild(script);
    };
  }, []);

  const toolConfig = {
    name: "PDF Repair Tool",
    description: "Repair corrupted and damaged PDF files using advanced recovery algorithms. Fix structural errors, recover content, and restore document accessibility.",
    acceptedFormats: [".pdf"],
    outputFormat: "PDF (Repaired)",
    maxFileSize: 500, // 500MB for potentially large corrupted files
  };

  const handleProcess = async (file: File): Promise<{ success: boolean; downloadUrl?: string; error?: string }> => {
    // Extended processing time for repair operations
    const processingTime = Math.max(5000, (file.size / 1024 / 1024) * 2000); // Minimum 5 seconds

    await new Promise(resolve => setTimeout(resolve, processingTime));

    // Simulate varying success rates based on repair complexity
    const successRate = repairMode === "aggressive" ? 0.75 : repairMode === "conservative" ? 0.85 : 0.80;

    if (Math.random() < successRate) {
      const blob = new Blob(['Mock repaired PDF content'], { type: 'application/pdf' });
      const downloadUrl = URL.createObjectURL(blob);

      return {
        success: true,
        downloadUrl: downloadUrl
      };
    } else {
      const errorMessages = [
        "PDF file is too severely corrupted to repair completely.",
        "Critical document structure damage detected. Partial recovery may be possible.",
        "File appears to be encrypted or password-protected rather than corrupted.",
        "Unable to locate valid PDF header. File may not be a PDF document.",
      ];

      return {
        success: false,
        error: errorMessages[Math.floor(Math.random() * errorMessages.length)]
      };
    }
  };

  const repairOptions = (
    <div className="space-y-6">
      {/* Repair Strategy */}
      <Card className="glassmorphism">
        <CardContent className="p-4">
          <h4 className="font-semibold mb-4 flex items-center">
            <Wrench className="mr-2" size={20} />
            Repair Strategy
          </h4>
          <div>
            <Label className="text-sm font-medium mb-2 block">Repair Mode</Label>
            <Select value={repairMode} onValueChange={setRepairMode}>
              <SelectTrigger data-testid="select-repair-mode">
                <SelectValue placeholder="Select repair mode" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="auto">Auto - Detect and Fix Issues</SelectItem>
                <SelectItem value="conservative">Conservative - Safe Repairs Only</SelectItem>
                <SelectItem value="aggressive">Aggressive - Maximum Recovery</SelectItem>
                <SelectItem value="structure">Structure Only - Fix Document Framework</SelectItem>
              </SelectContent>
            </Select>
            <div className="mt-2 text-xs text-muted-foreground">
              {repairMode === "auto" && "Automatically detects corruption type and applies appropriate repair methods."}
              {repairMode === "conservative" && "Uses safe repair methods with minimal risk of further damage."}
              {repairMode === "aggressive" && "Attempts maximum content recovery, may take longer processing time."}
              {repairMode === "structure" && "Focuses on repairing document structure and navigation elements."}
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Recovery Options */}
      <Card className="glassmorphism">
        <CardContent className="p-4">
          <h4 className="font-semibold mb-4">Recovery Options</h4>
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <div>
                <Label className="text-sm font-medium">Recover Text Content</Label>
                <p className="text-xs text-muted-foreground mt-1">Attempt to recover readable text from damaged areas</p>
              </div>
              <Switch
                checked={recoverText}
                onCheckedChange={setRecoverText}
                data-testid="switch-recover-text"
              />
            </div>

            <div className="flex items-center justify-between">
              <div>
                <Label className="text-sm font-medium">Recover Images</Label>
                <p className="text-xs text-muted-foreground mt-1">Extract and restore embedded images when possible</p>
              </div>
              <Switch
                checked={recoverImages}
                onCheckedChange={setRecoverImages}
                data-testid="switch-recover-images"
              />
            </div>

            <div className="flex items-center justify-between">
              <div>
                <Label className="text-sm font-medium">Rebuild Document Index</Label>
                <p className="text-xs text-muted-foreground mt-1">Reconstruct page index and navigation structure</p>
              </div>
              <Switch
                checked={rebuildIndex}
                onCheckedChange={setRebuildIndex}
                data-testid="switch-rebuild-index"
              />
            </div>

            <div className="flex items-center justify-between">
              <div>
                <Label className="text-sm font-medium">Remove Corrupted Elements</Label>
                <p className="text-xs text-muted-foreground mt-1">Remove irreparable elements to improve stability</p>
              </div>
              <Switch
                checked={removeCorruption}
                onCheckedChange={setRemoveCorruption}
                data-testid="switch-remove-corruption"
              />
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Diagnostic Information */}
      <div className="bg-amber-500/10 border border-amber-500/20 rounded-lg p-4">
        <h5 className="font-medium mb-3 flex items-center text-amber-600 dark:text-amber-400">
          <AlertTriangle className="mr-2" size={16} />
          Pre-Repair Analysis
        </h5>
        <div className="text-sm text-muted-foreground space-y-2">
          <p>â€¢ The repair tool will first analyze your PDF to identify corruption patterns</p>
          <p>â€¢ Common issues include: broken cross-references, damaged page objects, corrupted fonts</p>
          <p>â€¢ Processing time varies based on file size and corruption severity</p>
          <p>â€¢ Some content may be unrecoverable if severely damaged</p>
        </div>
      </div>

      {/* Repair Configuration Summary */}
      <div className="bg-primary/5 border border-primary/20 rounded-lg p-4">
        <h5 className="font-medium mb-3">Repair Configuration</h5>
        <div className="grid grid-cols-2 gap-4 text-sm">
          <div>
            <span className="text-muted-foreground">Mode:</span>
            <span className="ml-2 font-medium">
              {repairMode.charAt(0).toUpperCase() + repairMode.slice(1)}
            </span>
          </div>
          <div>
            <span className="text-muted-foreground">Text Recovery:</span>
            <span className="ml-2 font-medium">{recoverText ? "Enabled" : "Disabled"}</span>
          </div>
          <div>
            <span className="text-muted-foreground">Image Recovery:</span>
            <span className="ml-2 font-medium">{recoverImages ? "Enabled" : "Disabled"}</span>
          </div>
          <div>
            <span className="text-muted-foreground">Index Rebuild:</span>
            <span className="ml-2 font-medium">{rebuildIndex ? "Enabled" : "Disabled"}</span>
          </div>
        </div>
      </div>

      {/* Common PDF Issues */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
        <div className="bg-card/50 p-4 rounded-lg">
          <h4 className="font-medium mb-2 flex items-center">
            <CheckCircle2 className="mr-2 text-green-500" size={16} />
            Repairable Issues
          </h4>
          <ul className="text-muted-foreground space-y-1 text-xs">
            <li>â€¢ Broken cross-reference tables</li>
            <li>â€¢ Damaged page object streams</li>
            <li>â€¢ Corrupted font references</li>
            <li>â€¢ Missing or invalid PDF headers</li>
            <li>â€¢ Incomplete file transfers</li>
          </ul>
        </div>

        <div className="bg-card/50 p-4 rounded-lg">
          <h4 className="font-medium mb-2">Success Tips</h4>
          <ul className="text-muted-foreground space-y-1 text-xs">
            <li>â€¢ Try conservative mode first</li>
            <li>â€¢ Backup original before repair</li>
            <li>â€¢ Larger files may take longer</li>
            <li>â€¢ Some formatting may be lost</li>
            <li>â€¢ Multiple repair attempts may help</li>
          </ul>
        </div>
      </div>

      {/* Advanced Warning */}
      <div className="bg-red-500/10 border border-red-500/20 rounded-lg p-4">
        <h5 className="font-medium mb-2 text-red-600 dark:text-red-400">Important Notice</h5>
        <p className="text-sm text-muted-foreground">
          PDF repair is a complex process that cannot guarantee 100% recovery. Always keep a backup of your original 
          file before attempting repairs. Some visual formatting, fonts, or interactive elements may be lost or altered 
          during the repair process to restore basic document functionality.
        </p>
      </div>
    </div>
  );

  return (
    <UniversalToolInterface
      config={toolConfig}
      onProcess={handleProcess}
    >
      {repairOptions}
    </UniversalToolInterface>
  );
}

client/src/tools/pdf/PDFRotate.tsx.content:
import React, { useEffect, useState } from "react";
import UniversalToolInterface from "@/components/UniversalToolInterface";
import { Card, CardContent } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Button } from "@/components/ui/button";
import { Switch } from "@/components/ui/switch";
import { RotateCw, RotateCcw, RefreshCw } from "lucide-react";

export default function PDFRotate() {
  const [rotationAngle, setRotationAngle] = useState("90");
  const [rotationDirection, setRotationDirection] = useState("clockwise");
  const [pageSelection, setPageSelection] = useState("all");
  const [pageRange, setPageRange] = useState("");
  const [autoDetect, setAutoDetect] = useState(false);

  useEffect(() => {
    document.title = "Rotate PDF Pages - ToolSuite Pro | Fix PDF Page Orientation Online";

    const metaDescription = document.querySelector('meta[name="description"]');
    if (metaDescription) {
      metaDescription.setAttribute('content', 'Rotate PDF pages to correct orientation online. Fix upside-down or sideways pages with precise angle control, batch rotation, and auto-orientation detection.');
    }

    const structuredData = {
      "@context": "https://schema.org",
      "@type": "WebApplication",
      "name": "PDF Page Rotation Tool",
      "description": "Rotate PDF pages to correct orientation and fix page alignment",
      "url": window.location.href,
      "applicationCategory": "ProductivityApplication",
      "operatingSystem": "Any",
      "offers": {
        "@type": "Offer",
        "price": "0",
        "priceCurrency": "USD"
      },
      "featureList": [
        "Rotate individual or all pages",
        "Custom angle rotation",
        "Auto-orientation detection",
        "Batch page processing",
        "Precise orientation control"
      ]
    };

    const script = document.createElement('script');
    script.type = 'application/ld+json';
    script.textContent = JSON.stringify(structuredData);
    document.head.appendChild(script);

    return () => {
      document.head.removeChild(script);
    };
  }, []);

  const toolConfig = {
    name: "PDF Page Rotation Tool",
    description: "Rotate PDF pages to correct orientation and fix page alignment. Support for custom angles, batch processing, and automatic orientation detection.",
    acceptedFormats: [".pdf"],
    outputFormat: "PDF (Rotated)",
    maxFileSize: 200,
  };

  const handleProcess = async (file: File): Promise<{ success: boolean; downloadUrl?: string; error?: string }> => {
    if (pageSelection === "range" && pageRange && !/^(\d+(-\d+)?,?\s*)+$/.test(pageRange.replace(/\s/g, ''))) {
      return {
        success: false,
        error: "Invalid page range format. Use format like: 1-5, 7, 10-12"
      };
    }

    const processingTime = Math.max(2000, (file.size / 1024 / 1024) * 600);
    await new Promise(resolve => setTimeout(resolve, processingTime));

    if (Math.random() > 0.03) {
      const blob = new Blob(['Mock rotated PDF content'], { type: 'application/pdf' });
      const downloadUrl = URL.createObjectURL(blob);

      return {
        success: true,
        downloadUrl: downloadUrl
      };
    } else {
      return {
        success: false,
        error: "Failed to rotate PDF pages. The file may be corrupted or password-protected."
      };
    }
  };

  const rotationOptions = (
    <div className="space-y-6">
      {/* Quick Rotation */}
      <Card className="glassmorphism">
        <CardContent className="p-4">
          <h4 className="font-semibold mb-4">Quick Rotation</h4>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
            <Button
              variant={rotationAngle === "90" && rotationDirection === "clockwise" ? "default" : "outline"}
              onClick={() => {
                setRotationAngle("90");
                setRotationDirection("clockwise");
              }}
              className="h-16 flex-col gap-2"
              data-testid="button-rotate-90-cw"
            >
              <RotateCw size={20} />
              <span className="text-xs">90Â° CW</span>
            </Button>

            <Button
              variant={rotationAngle === "180" ? "default" : "outline"}
              onClick={() => setRotationAngle("180")}
              className="h-16 flex-col gap-2"
              data-testid="button-rotate-180"
            >
              <RefreshCw size={20} />
              <span className="text-xs">180Â°</span>
            </Button>

            <Button
              variant={rotationAngle === "90" && rotationDirection === "counterclockwise" ? "default" : "outline"}
              onClick={() => {
                setRotationAngle("90");
                setRotationDirection("counterclockwise");
              }}
              className="h-16 flex-col gap-2"
              data-testid="button-rotate-90-ccw"
            >
              <RotateCcw size={20} />
              <span className="text-xs">90Â° CCW</span>
            </Button>

            <Button
              variant={rotationAngle === "270" ? "default" : "outline"}
              onClick={() => setRotationAngle("270")}
              className="h-16 flex-col gap-2"
              data-testid="button-rotate-270"
            >
              <RotateCcw size={20} className="transform rotate-180" />
              <span className="text-xs">270Â°</span>
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Custom Rotation */}
      <Card className="glassmorphism">
        <CardContent className="p-4">
          <h4 className="font-semibold mb-4">Custom Rotation</h4>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <Label className="text-sm font-medium mb-2 block">Rotation Angle</Label>
              <Select value={rotationAngle} onValueChange={setRotationAngle}>
                <SelectTrigger data-testid="select-rotation-angle">
                  <SelectValue placeholder="Select angle" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="90">90 degrees</SelectItem>
                  <SelectItem value="180">180 degrees</SelectItem>
                  <SelectItem value="270">270 degrees</SelectItem>
                  <SelectItem value="custom">Custom angle</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {rotationAngle !== "180" && rotationAngle !== "custom" && (
              <div>
                <Label className="text-sm font-medium mb-2 block">Direction</Label>
                <Select value={rotationDirection} onValueChange={setRotationDirection}>
                  <SelectTrigger data-testid="select-rotation-direction">
                    <SelectValue placeholder="Select direction" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="clockwise">Clockwise</SelectItem>
                    <SelectItem value="counterclockwise">Counter-clockwise</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            )}

            {rotationAngle === "custom" && (
              <div>
                <Label className="text-sm font-medium mb-2 block">Custom Angle (degrees)</Label>
                <Input
                  type="number"
                  min="-360"
                  max="360"
                  step="1"
                  placeholder="Enter angle (e.g., 45)"
                  className="w-full"
                  data-testid="input-custom-angle"
                />
                <p className="text-xs text-muted-foreground mt-1">
                  Positive values rotate clockwise, negative values counter-clockwise
                </p>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Page Selection */}
      <Card className="glassmorphism">
        <CardContent className="p-4">
          <h4 className="font-semibold mb-4">Page Selection</h4>
          <div className="space-y-4">
            <div>
              <Label className="text-sm font-medium mb-2 block">Apply Rotation To</Label>
              <Select value={pageSelection} onValueChange={setPageSelection}>
                <SelectTrigger data-testid="select-page-selection">
                  <SelectValue placeholder="Select pages" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Pages</SelectItem>
                  <SelectItem value="odd">Odd Pages Only</SelectItem>
                  <SelectItem value="even">Even Pages Only</SelectItem>
                  <SelectItem value="first">First Page Only</SelectItem>
                  <SelectItem value="last">Last Page Only</SelectItem>
                  <SelectItem value="range">Custom Page Range</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {pageSelection === "range" && (
              <div>
                <Label className="text-sm font-medium mb-2 block">Page Range</Label>
                <Input
                  type="text"
                  placeholder="e.g., 1-5, 7, 10-12"
                  value={pageRange}
                  onChange={(e) => setPageRange(e.target.value)}
                  className="w-full"
                  data-testid="input-page-range"
                />
                <p className="text-xs text-muted-foreground mt-1">
                  Use commas to separate ranges: 1-5, 7, 10-12
                </p>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Auto-Detection */}
      <Card className="glassmorphism">
        <CardContent className="p-4">
          <h4 className="font-semibold mb-4">Smart Features</h4>
          <div className="flex items-center justify-between">
            <div>
              <Label className="text-sm font-medium">Auto-Detect Orientation</Label>
              <p className="text-xs text-muted-foreground mt-1">
                Automatically detect and correct page orientation based on text analysis
              </p>
            </div>
            <Switch
              checked={autoDetect}
              onCheckedChange={setAutoDetect}
              data-testid="switch-auto-detect"
            />
          </div>

          {autoDetect && (
            <div className="mt-4 bg-blue-500/10 border border-blue-500/20 rounded-lg p-3">
              <p className="text-sm text-muted-foreground">
                <strong className="text-blue-600 dark:text-blue-400">Auto-Detection Enabled:</strong> The system will analyze text orientation 
                and automatically rotate pages to the correct reading orientation, overriding manual rotation settings.
              </p>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Rotation Preview */}
      <div className="bg-primary/5 border border-primary/20 rounded-lg p-4">
        <h5 className="font-medium mb-3">Rotation Summary</h5>
        <div className="grid grid-cols-2 gap-4 text-sm">
          <div>
            <span className="text-muted-foreground">Angle:</span>
            <span className="ml-2 font-medium">
              {rotationAngle}Â° {rotationAngle !== "180" && rotationAngle !== "custom" ? 
                `(${rotationDirection})` : ''}
            </span>
          </div>
          <div>
            <span className="text-muted-foreground">Pages:</span>
            <span className="ml-2 font-medium">
              {pageSelection === "all" ? "All pages" :
               pageSelection === "odd" ? "Odd pages" :
               pageSelection === "even" ? "Even pages" :
               pageSelection === "first" ? "First page" :
               pageSelection === "last" ? "Last page" :
               pageRange || "Custom range"}
