                    <span className="text-sm text-muted-foreground">{pitch[0]}</span>
                  </div>
                  <Slider
                    value={pitch}
                    onValueChange={setPitch}
                    min={0}
                    max={2}
                    step={0.1}
                    className="w-full"
                  />
                </div>

                {/* Volume Control */}
                <div>
                  <div className="flex justify-between items-center mb-2">
                    <label className="text-sm font-medium">Volume</label>
                    <span className="text-sm text-muted-foreground">{Math.round(volume[0] * 100)}%</span>
                  </div>
                  <Slider
                    value={volume}
                    onValueChange={setVolume}
                    min={0}
                    max={1}
                    step={0.1}
                    className="w-full"
                  />
                </div>
              </CardContent>
            </Card>

            {/* Control Buttons */}
            <Card className="glassmorphism">
              <CardContent className="pt-6">
                <div className="space-y-3">
                  {!isPlaying ? (
                    <Button
                      onClick={playText}
                      className="w-full gradient-bg"
                      size="lg"
                    >
                      <Play className="mr-2" size={16} />
                      Play
                    </Button>
                  ) : (
                    <div className="space-y-2">
                      <Button
                        onClick={isPaused ? playText : pauseText}
                        className="w-full"
                        variant="outline"
                      >
                        {isPaused ? <Play className="mr-2" size={16} /> : <Pause className="mr-2" size={16} />}
                        {isPaused ? 'Resume' : 'Pause'}
                      </Button>
                      <Button
                        onClick={stopText}
                        className="w-full"
                        variant="destructive"
                      >
                        <VolumeX className="mr-2" size={16} />
                        Stop
                      </Button>
                    </div>
                  )}

                  <Button
                    onClick={downloadAudio}
                    className="w-full"
                    variant="outline"
                    disabled={!text.trim()}
                  >
                    <Download className="mr-2" size={16} />
                    Download Audio
                  </Button>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>

        <AdSlot position="tool-bottom" page="universal-tool" size="banner" className="mt-8" />
      </div>
    </div>
  );
}

