                      </div>
                    </TabsContent>
                  </Tabs>
                </CardContent>
              </Card>
            )}
          </div>

          {/* Statistics Sidebar */}
          <div className="space-y-6">
            {statistics && (
              <Card className="glassmorphism">
                <CardHeader>
                  <CardTitle className="flex items-center">
                    <BarChart3 className="mr-2" size={20} />
                    Text Statistics
