import { useEffect, useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Receipt, Download, Plus, Trash2, Calculator, Printer } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import AdSlot from "@/components/AdSlot";

interface InvoiceItem {
  id: string;
  description: string;
  quantity: number;
  price: number;
}

interface InvoiceData {
  invoiceNumber: string;
  date: string;
  dueDate: string;
  companyName: string;
  companyAddress: string;
  companyEmail: string;
  companyPhone: string;
  clientName: string;
  clientAddress: string;
  clientEmail: string;
  items: InvoiceItem[];
  taxRate: number;
  notes: string;
}

export default function InvoiceGenerator() {
  const { toast } = useToast();
  const [invoice, setInvoice] = useState<InvoiceData>({
    invoiceNumber: `INV-${Date.now().toString().slice(-6)}`,
    date: new Date().toISOString().split('T')[0],
    dueDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
    companyName: '',
    companyAddress: '',
    companyEmail: '',
    companyPhone: '',
    clientName: '',
    clientAddress: '',
    clientEmail: '',
    items: [{ id: '1', description: '', quantity: 1, price: 0 }],
    taxRate: 0,
    notes: ''
  });

  useEffect(() => {
    document.title = "Invoice Generator - ToolSuite Pro | Create Professional Invoices";

    const metaDescription = document.querySelector('meta[name="description"]');
    if (metaDescription) {
      metaDescription.setAttribute('content', 'Professional invoice generator to create, customize and download business invoices. Generate PDF invoices with tax calculations, item listings, and professional formatting.');
    }

    // Add structured data
    const structuredData = {
      "@context": "https://schema.org",
      "@type": "WebApplication",
      "name": "Invoice Generator",
      "description": "Professional invoice generator tool",
      "applicationCategory": "BusinessApplication",
      "operatingSystem": "Web Browser",
      "offers": {
        "@type": "Offer",
        "price": "0",
        "priceCurrency": "USD"
      }
    };

    const script = document.createElement('script');
    script.type = 'application/ld+json';
    script.text = JSON.stringify(structuredData);
    document.head.appendChild(script);

    return () => {
      document.head.removeChild(script);
    };
  }, []);

  const addItem = () => {
    const newItem: InvoiceItem = {
      id: Date.now().toString(),
      description: '',
      quantity: 1,
      price: 0
    };
    setInvoice(prev => ({
      ...prev,
      items: [...prev.items, newItem]
    }));
  };

  const removeItem = (id: string) => {
    if (invoice.items.length > 1) {
      setInvoice(prev => ({
        ...prev,
        items: prev.items.filter(item => item.id !== id)
      }));
    }
  };

  const updateItem = (id: string, field: keyof InvoiceItem, value: string | number) => {
    setInvoice(prev => ({
      ...prev,
      items: prev.items.map(item =>
        item.id === id ? { ...item, [field]: value } : item
      )
    }));
  };

  const calculateSubtotal = () => {
    return invoice.items.reduce((sum, item) => sum + (item.quantity * item.price), 0);
  };

  const calculateTax = () => {
    return (calculateSubtotal() * invoice.taxRate) / 100;
  };

  const calculateTotal = () => {
    return calculateSubtotal() + calculateTax();
  };

  const generateInvoiceHTML = () => {
    const subtotal = calculateSubtotal();
    const tax = calculateTax();
    const total = calculateTotal();

    return `
<!DOCTYPE html>
<html>
<head>
    <meta charset="UTF-8">
    <title>Invoice ${invoice.invoiceNumber}</title>
    <style>
        body { font-family: Arial, sans-serif; margin: 0; padding: 20px; color: #333; }
        .invoice-header { display: flex; justify-content: space-between; margin-bottom: 30px; }
        .company-info h1 { color: #2563eb; margin: 0 0 10px 0; }
        .invoice-details { text-align: right; }
        .invoice-details h2 { color: #2563eb; margin: 0; }
        .billing-info { display: flex; justify-content: space-between; margin-bottom: 30px; }
        .billing-section h3 { color: #374151; margin: 0 0 10px 0; }
        .items-table { width: 100%; border-collapse: collapse; margin-bottom: 20px; }
        .items-table th { background-color: #f3f4f6; padding: 12px; text-align: left; border-bottom: 2px solid #e5e7eb; }
        .items-table td { padding: 12px; border-bottom: 1px solid #e5e7eb; }
        .text-right { text-align: right; }
        .totals { margin-left: auto; width: 300px; }
        .totals-row { display: flex; justify-content: space-between; padding: 8px 0; }
        .total-row { font-weight: bold; font-size: 1.1em; border-top: 2px solid #374151; padding-top: 10px; }
        .notes { margin-top: 30px; }
        .notes h3 { color: #374151; }
    </style>
</head>
<body>
    <div class="invoice-header">
        <div class="company-info">
            <h1>${invoice.companyName || 'Your Company'}</h1>
            <p>${invoice.companyAddress.replace(/\n/g, '<br>')}</p>
            <p>Email: ${invoice.companyEmail}</p>
            <p>Phone: ${invoice.companyPhone}</p>
        </div>
        <div class="invoice-details">
            <h2>INVOICE</h2>
            <p><strong>Invoice #:</strong> ${invoice.invoiceNumber}</p>
            <p><strong>Date:</strong> ${new Date(invoice.date).toLocaleDateString()}</p>
            <p><strong>Due Date:</strong> ${new Date(invoice.dueDate).toLocaleDateString()}</p>
        </div>
    </div>

    <div class="billing-info">
        <div class="bill-to">
            <h3>Bill To:</h3>
            <p><strong>${invoice.clientName}</strong></p>
            <p>${invoice.clientAddress.replace(/\n/g, '<br>')}</p>
            <p>${invoice.clientEmail}</p>
        </div>
    </div>

    <table class="items-table">
        <thead>
            <tr>
                <th>Description</th>
                <th class="text-right">Quantity</th>
                <th class="text-right">Price</th>
                <th class="text-right">Total</th>
            </tr>
        </thead>
        <tbody>
            ${invoice.items.map(item => `
                <tr>
                    <td>${item.description}</td>
                    <td class="text-right">${item.quantity}</td>
                    <td class="text-right">$${item.price.toFixed(2)}</td>
                    <td class="text-right">$${(item.quantity * item.price).toFixed(2)}</td>
                </tr>
            `).join('')}
        </tbody>
    </table>

    <div class="totals">
        <div class="totals-row">
            <span>Subtotal:</span>
            <span>$${subtotal.toFixed(2)}</span>
        </div>
        ${invoice.taxRate > 0 ? `
        <div class="totals-row">
            <span>Tax (${invoice.taxRate}%):</span>
            <span>$${tax.toFixed(2)}</span>
        </div>
        ` : ''}
        <div class="totals-row total-row">
            <span>Total:</span>
            <span>$${total.toFixed(2)}</span>
        </div>
    </div>

    ${invoice.notes ? `
    <div class="notes">
        <h3>Notes:</h3>
        <p>${invoice.notes.replace(/\n/g, '<br>')}</p>
    </div>
    ` : ''}
</body>
</html>`;
  };

  const downloadInvoice = () => {
    const html = generateInvoiceHTML();
    const blob = new Blob([html], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `invoice-${invoice.invoiceNumber}.html`;
    a.click();
    URL.revokeObjectURL(url);

    toast({
      title: "Invoice Downloaded!",
      description: "Your invoice has been downloaded as an HTML file.",
    });
  };

  const printInvoice = () => {
    const html = generateInvoiceHTML();
    const printWindow = window.open('', '_blank');
    if (printWindow) {
      printWindow.document.write(html);
      printWindow.document.close();
      printWindow.print();
    }
  };

  const clearForm = () => {
    setInvoice({
      invoiceNumber: `INV-${Date.now().toString().slice(-6)}`,
      date: new Date().toISOString().split('T')[0],
      dueDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
      companyName: '',
      companyAddress: '',
      companyEmail: '',
      companyPhone: '',
      clientName: '',
      clientAddress: '',
      clientEmail: '',
      items: [{ id: '1', description: '', quantity: 1, price: 0 }],
      taxRate: 0,
      notes: ''
    });
  };

  return (
    <div className="min-h-screen py-8 px-4">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center mb-4">
            <div className="gradient-bg p-3 rounded-lg mr-4">
              <Receipt className="text-white" size={32} />
            </div>
            <h1 className="text-4xl md:text-5xl font-bold gradient-text">Invoice Generator</h1>
          </div>
          <p className="text-muted-foreground text-lg max-w-2xl mx-auto">
            Create professional invoices with automatic calculations, custom branding, and instant download. 
            Perfect for freelancers and small businesses.
          </p>
        </div>

        {/* Ad Slot */}
        <AdSlot position="invoice-generator-top" size="banner" />

        <div className="space-y-8">
          {/* Invoice Form */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            {/* Company Information */}
            <Card>
              <CardHeader>
                <CardTitle>Company Information</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label htmlFor="company-name">Company Name</Label>
                  <Input
                    id="company-name"
                    value={invoice.companyName}
                    onChange={(e) => setInvoice(prev => ({...prev, companyName: e.target.value}))}
                    placeholder="Your Company Name"
                  />
                </div>
                <div>
                  <Label htmlFor="company-address">Address</Label>
                  <Textarea
                    id="company-address"
                    value={invoice.companyAddress}
                    onChange={(e) => setInvoice(prev => ({...prev, companyAddress: e.target.value}))}
                    placeholder="123 Main St&#10;City, State 12345"
                    rows={3}
                  />
                </div>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="company-email">Email</Label>
                    <Input
                      id="company-email"
                      type="email"
                      value={invoice.companyEmail}
                      onChange={(e) => setInvoice(prev => ({...prev, companyEmail: e.target.value}))}
                      placeholder="company@example.com"
                    />
                  </div>
                  <div>
                    <Label htmlFor="company-phone">Phone</Label>
                    <Input
                      id="company-phone"
                      value={invoice.companyPhone}
                      onChange={(e) => setInvoice(prev => ({...prev, companyPhone: e.target.value}))}
                      placeholder="(555) 123-4567"
                    />
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Invoice Details */}
            <Card>
              <CardHeader>
                <CardTitle>Invoice Details</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label htmlFor="invoice-number">Invoice Number</Label>
                  <Input
                    id="invoice-number"
                    value={invoice.invoiceNumber}
                    onChange={(e) => setInvoice(prev => ({...prev, invoiceNumber: e.target.value}))}
                  />
                </div>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="date">Date</Label>
                    <Input
                      id="date"
                      type="date"
                      value={invoice.date}
                      onChange={(e) => setInvoice(prev => ({...prev, date: e.target.value}))}
                    />
                  </div>
                  <div>
                    <Label htmlFor="due-date">Due Date</Label>
                    <Input
                      id="due-date"
                      type="date"
                      value={invoice.dueDate}
                      onChange={(e) => setInvoice(prev => ({...prev, dueDate: e.target.value}))}
                    />
                  </div>
                </div>
                <div>
                  <Label htmlFor="tax-rate">Tax Rate (%)</Label>
                  <Input
                    id="tax-rate"
                    type="number"
                    min="0"
                    max="100"
                    step="0.01"
                    value={invoice.taxRate}
                    onChange={(e) => setInvoice(prev => ({...prev, taxRate: parseFloat(e.target.value) || 0}))}
                  />
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Client Information */}
          <Card>
            <CardHeader>
              <CardTitle>Bill To</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                <div>
                  <Label htmlFor="client-name">Client Name</Label>
                  <Input
                    id="client-name"
                    value={invoice.clientName}
                    onChange={(e) => setInvoice(prev => ({...prev, clientName: e.target.value}))}
                    placeholder="Client Name"
                  />
                </div>
                <div>
                  <Label htmlFor="client-email">Client Email</Label>
                  <Input
                    id="client-email"
                    type="email"
                    value={invoice.clientEmail}
                    onChange={(e) => setInvoice(prev => ({...prev, clientEmail: e.target.value}))}
                    placeholder="client@example.com"
                  />
                </div>
                <div>
                  <Label htmlFor="client-address">Client Address</Label>
                  <Textarea
                    id="client-address"
                    value={invoice.clientAddress}
                    onChange={(e) => setInvoice(prev => ({...prev, clientAddress: e.target.value}))}
                    placeholder="Client Address"
                    rows={1}
                  />
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Items */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center justify-between">
                Items
                <Button onClick={addItem} size="sm">
                  <Plus size={16} className="mr-2" />
                  Add Item
                </Button>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {invoice.items.map((item, index) => (
                  <div key={item.id} className="grid grid-cols-1 md:grid-cols-12 gap-4 p-4 border rounded-lg">
                    <div className="md:col-span-5">
                      <Label>Description</Label>
                      <Input
                        value={item.description}
                        onChange={(e) => updateItem(item.id, 'description', e.target.value)}
                        placeholder="Item description"
                      />
                    </div>
                    <div className="md:col-span-2">
                      <Label>Quantity</Label>
                      <Input
                        type="number"
                        min="1"
                        value={item.quantity}
                        onChange={(e) => updateItem(item.id, 'quantity', parseInt(e.target.value) || 1)}
                      />
                    </div>
                    <div className="md:col-span-2">
                      <Label>Price ($)</Label>
                      <Input
                        type="number"
                        min="0"
                        step="0.01"
                        value={item.price}
                        onChange={(e) => updateItem(item.id, 'price', parseFloat(e.target.value) || 0)}
                      />
                    </div>
                    <div className="md:col-span-2">
                      <Label>Total</Label>
                      <div className="h-10 flex items-center font-mono font-semibold">
                        ${(item.quantity * item.price).toFixed(2)}
                      </div>
                    </div>
                    <div className="md:col-span-1 flex items-end">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => removeItem(item.id)}
                        disabled={invoice.items.length === 1}
                        className="w-full"
                      >
                        <Trash2 size={16} />
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>

          {/* Totals */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Calculator className="mr-2" size={20} />
                Invoice Summary
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="max-w-md ml-auto space-y-2">
                <div className="flex justify-between">
                  <span>Subtotal:</span>
                  <span className="font-mono">${calculateSubtotal().toFixed(2)}</span>
                </div>
                {invoice.taxRate > 0 && (
                  <div className="flex justify-between">
                    <span>Tax ({invoice.taxRate}%):</span>
                    <span className="font-mono">${calculateTax().toFixed(2)}</span>
                  </div>
                )}
                <Separator />
                <div className="flex justify-between text-lg font-bold">
                  <span>Total:</span>
                  <span className="font-mono">${calculateTotal().toFixed(2)}</span>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Notes */}
          <Card>
            <CardHeader>
              <CardTitle>Notes</CardTitle>
            </CardHeader>
            <CardContent>
              <Textarea
                value={invoice.notes}
                onChange={(e) => setInvoice(prev => ({...prev, notes: e.target.value}))}
                placeholder="Additional notes or payment terms..."
                rows={4}
              />
            </CardContent>
          </Card>

          {/* Actions */}
          <div className="flex flex-wrap gap-4 justify-center">
            <Button onClick={downloadInvoice} className="gradient-bg">
              <Download size={16} className="mr-2" />
              Download Invoice
            </Button>
            <Button onClick={printInvoice} variant="outline">
              <Printer size={16} className="mr-2" />
              Print Invoice
            </Button>
            <Button onClick={clearForm} variant="outline">
              Clear Form
            </Button>
          </div>
        </div>

        {/* Ad Slot */}
        <div className="mt-8">
          <AdSlot position="invoice-generator-bottom" size="banner" />
        </div>
      </div>
    </div>
  );
}

client/src/tools/productivity/NoteTaking.content:
import { useEffect, useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Save, Download, Trash2, Search, Plus, StickyNote, Clock, Tag } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import AdSlot from "@/components/AdSlot";

interface Note {
  id: string;
  title: string;
  content: string;
  tags: string[];
  createdAt: string;
  updatedAt: string;
  category: string;
}

const categories = [
  'General',
  'Work',
  'Personal',
  'Ideas',
  'Todo',
  'Meeting Notes',
  'Research',
  'Projects'
];

const tagColors = [
  'bg-red-100 text-red-800',
  'bg-blue-100 text-blue-800',
  'bg-green-100 text-green-800',
  'bg-yellow-100 text-yellow-800',
  'bg-purple-100 text-purple-800',
  'bg-pink-100 text-pink-800',
  'bg-indigo-100 text-indigo-800',
  'bg-cyan-100 text-cyan-800'
];

export default function NoteTaking() {
  const { toast } = useToast();
  const [notes, setNotes] = useState<Note[]>([]);
  const [currentNote, setCurrentNote] = useState<Note>({
    id: '',
    title: '',
    content: '',
    tags: [],
    createdAt: '',
    updatedAt: '',
    category: 'General'
  });
  const [selectedNote, setSelectedNote] = useState<string | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [newTag, setNewTag] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');

  useEffect(() => {
    document.title = "Note Taking - ToolSuite Pro | Quick Notes with Rich Text Formatting";

    const metaDescription = document.querySelector('meta[name="description"]');
    if (metaDescription) {
      metaDescription.setAttribute('content', 'Professional note-taking app with rich text formatting, tags, categories, and search. Organize your thoughts, ideas, and important information efficiently.');
    }

    // Add structured data
    const structuredData = {
      "@context": "https://schema.org",
      "@type": "WebApplication",
      "name": "Note Taking App",
      "description": "Professional note-taking and organization tool",
      "applicationCategory": "ProductivityApplication",
      "operatingSystem": "Web Browser",
      "offers": {
        "@type": "Offer",
        "price": "0",
        "priceCurrency": "USD"
      }
    };

    const script = document.createElement('script');
    script.type = 'application/ld+json';
    script.text = JSON.stringify(structuredData);
    document.head.appendChild(script);

    // Load notes from localStorage
    const savedNotes = localStorage.getItem('notes');
    if (savedNotes) {
      setNotes(JSON.parse(savedNotes));
    }

    return () => {
      document.head.removeChild(script);
    };
  }, []);

  useEffect(() => {
    // Save notes to localStorage whenever notes change
    localStorage.setItem('notes', JSON.stringify(notes));
  }, [notes]);

  const createNewNote = () => {
    const now = new Date().toLocaleString();
    const newNote: Note = {
      id: Date.now().toString(),
      title: 'New Note',
      content: '',
      tags: [],
      createdAt: now,
      updatedAt: now,
      category: 'General'
    };

    setCurrentNote(newNote);
    setSelectedNote(newNote.id);
  };

  const saveNote = () => {
    if (!currentNote.title.trim() && !currentNote.content.trim()) {
      toast({
        title: "Cannot Save",
        description: "Please add a title or content to save the note",
        variant: "destructive",
      });
      return;
    }

    const now = new Date().toLocaleString();
    const noteToSave = {
      ...currentNote,
      updatedAt: now,
      createdAt: currentNote.createdAt || now
    };

    const existingIndex = notes.findIndex(note => note.id === currentNote.id);

    if (existingIndex >= 0) {
      // Update existing note
      setNotes(prev => prev.map(note => 
        note.id === currentNote.id ? noteToSave : note
      ));
      toast({
        title: "Note Updated",
        description: "Your note has been saved successfully",
      });
    } else {
      // Add new note
      setNotes(prev => [noteToSave, ...prev]);
      toast({
        title: "Note Saved",
        description: "Your new note has been created",
      });
    }
  };

  const deleteNote = (noteId: string) => {
    setNotes(prev => prev.filter(note => note.id !== noteId));
    if (selectedNote === noteId) {
      setCurrentNote({
        id: '',
        title: '',
        content: '',
        tags: [],
        createdAt: '',
        updatedAt: '',
        category: 'General'
      });
      setSelectedNote(null);
    }
    toast({
      title: "Note Deleted",
      description: "Note has been removed",
    });
  };

  const selectNote = (note: Note) => {
    setCurrentNote(note);
    setSelectedNote(note.id);
  };

  const addTag = () => {
    if (newTag.trim() && !currentNote.tags.includes(newTag.trim())) {
      setCurrentNote(prev => ({
        ...prev,
        tags: [...prev.tags, newTag.trim()]
      }));
      setNewTag('');
    }
  };

  const removeTag = (tagToRemove: string) => {
    setCurrentNote(prev => ({
      ...prev,
      tags: prev.tags.filter(tag => tag !== tagToRemove)
    }));
  };

  const getTagColor = (index: number) => {
    return tagColors[index % tagColors.length];
  };

  const filteredNotes = notes.filter(note => {
    const matchesSearch = note.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         note.content.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         note.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesCategory = selectedCategory === 'All' || note.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const exportNote = (note: Note) => {
    const content = `# ${note.title}\n\n${note.content}\n\n---\nCreated: ${note.createdAt}\nUpdated: ${note.updatedAt}\nCategory: ${note.category}\nTags: ${note.tags.join(', ')}`;
    const blob = new Blob([content], { type: 'text/markdown' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${note.title.replace(/[^a-zA-Z0-9]/g, '-')}.md`;
    a.click();
    URL.revokeObjectURL(url);

    toast({
      title: "Note Exported",
      description: "Note has been downloaded as Markdown",
    });
  };

  const exportAllNotes = () => {
    const content = notes.map(note => 
      `# ${note.title}\n\n${note.content}\n\n---\nCreated: ${note.createdAt}\nUpdated: ${note.updatedAt}\nCategory: ${note.category}\nTags: ${note.tags.join(', ')}\n\n---\n\n`
    ).join('');

    const blob = new Blob([content], { type: 'text/markdown' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `all-notes-${new Date().toISOString().split('T')[0]}.md`;
    a.click();
    URL.revokeObjectURL(url);

    toast({
      title: "All Notes Exported",
      description: "All notes have been downloaded as Markdown",
    });
  };

  const clearAllNotes = () => {
    setNotes([]);
    setCurrentNote({
      id: '',
      title: '',
      content: '',
      tags: [],
      createdAt: '',
      updatedAt: '',
      category: 'General'
    });
    setSelectedNote(null);
    localStorage.removeItem('notes');

    toast({
      title: "All Notes Cleared",
      description: "All notes have been deleted",
    });
  };

  return (
    <div className="min-h-screen py-8 px-4">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center mb-4">
            <div className="gradient-bg p-3 rounded-lg mr-4">
              <StickyNote className="text-white" size={32} />
            </div>
            <h1 className="text-4xl md:text-5xl font-bold gradient-text">Note Taking</h1>
          </div>
          <p className="text-muted-foreground text-lg max-w-2xl mx-auto">
            Capture your thoughts, ideas, and important information with our professional note-taking app. 
            Organize with tags, categories, and powerful search.
          </p>
        </div>

        {/* Ad Slot */}
        <AdSlot position="note-taking-top" page="productivity-tools" size="banner" />

        <div className="space-y-6">
          {/* Toolbar */}
          <Card>
            <CardContent className="p-4">
              <div className="flex flex-wrap items-center gap-4">
                <Button onClick={createNewNote} className="gradient-bg">
                  <Plus size={16} className="mr-2" />
                  New Note
                </Button>

                <div className="flex items-center space-x-2">
                  <Search size={16} className="text-muted-foreground" />
                  <Input
                    placeholder="Search notes..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-64"
                  />
                </div>

                <select
                  value={selectedCategory}
                  onChange={(e) => setSelectedCategory(e.target.value)}
                  className="px-3 py-2 border rounded-md bg-background"
                >
                  <option value="All">All Categories</option>
                  {categories.map(category => (
                    <option key={category} value={category}>{category}</option>
                  ))}
                </select>

                {notes.length > 0 && (
                  <>
                    <Button onClick={exportAllNotes} variant="outline" size="sm">
                      <Download size={16} className="mr-2" />
                      Export All
                    </Button>
                    <Button onClick={clearAllNotes} variant="outline" size="sm">
                      <Trash2 size={16} className="mr-2" />
                      Clear All
                    </Button>
                  </>
                )}
              </div>
            </CardContent>
          </Card>

          {/* Main Content */}
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            {/* Notes List */}
            <Card className="lg:col-span-1">
              <CardHeader>
                <CardTitle className="flex items-center">
                  <StickyNote className="mr-2" size={20} />
                  Notes ({filteredNotes.length})
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-3 max-h-96 overflow-y-auto">
                  {filteredNotes.length > 0 ? (
                    filteredNotes.map((note) => (
                      <div
                        key={note.id}
                        className={`p-3 border rounded-lg cursor-pointer transition-colors ${
                          selectedNote === note.id ? 'border-primary bg-primary/5' : 'hover:bg-muted'
                        }`}
                        onClick={() => selectNote(note)}
                      >
                        <div className="flex items-start justify-between">
                          <div className="flex-1 min-w-0">
                            <h4 className="font-medium truncate">{note.title}</h4>
                            <p className="text-sm text-muted-foreground truncate">
                              {note.content || 'No content'}
                            </p>
                            <div className="flex items-center mt-2 space-x-2 text-xs text-muted-foreground">
                              <Clock size={12} />
                              <span>{note.updatedAt}</span>
                            </div>
                            {note.tags.length > 0 && (
                              <div className="flex flex-wrap gap-1 mt-2">
                                {note.tags.slice(0, 2).map((tag, index) => (
                                  <Badge key={tag} variant="secondary" className="text-xs">
                                    {tag}
                                  </Badge>
                                ))}
                                {note.tags.length > 2 && (
                                  <Badge variant="secondary" className="text-xs">
                                    +{note.tags.length - 2}
                                  </Badge>
                                )}
                              </div>
                            )}
                          </div>
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={(e) => {
                              e.stopPropagation();
                              deleteNote(note.id);
                            }}
                          >
                            <Trash2 size={14} />
                          </Button>
                        </div>
                      </div>
                    ))
                  ) : (
                    <div className="text-center py-8 text-muted-foreground">
                      {searchTerm ? 'No notes match your search' : 'No notes yet. Create your first note!'}
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>

            {/* Note Editor */}
            <Card className="lg:col-span-2">
              <CardHeader>
                <CardTitle className="flex items-center justify-between">
                  <span>
                    {selectedNote ? 'Edit Note' : 'New Note'}
                  </span>
                  <div className="flex space-x-2">
                    <Button onClick={saveNote} size="sm">
                      <Save size={16} className="mr-2" />
                      Save
                    </Button>
                    {selectedNote && (
                      <Button
                        onClick={() => {
                          const note = notes.find(n => n.id === selectedNote);
                          if (note) exportNote(note);
                        }}
                        size="sm"
                        variant="outline"
                      >
                        <Download size={16} className="mr-2" />
                        Export
                      </Button>
                    )}
                  </div>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label htmlFor="note-title">Title</Label>
                  <Input
                    id="note-title"
                    value={currentNote.title}
                    onChange={(e) => setCurrentNote(prev => ({ ...prev, title: e.target.value }))}
                    placeholder="Enter note title..."
                  />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="note-category">Category</Label>
                    <select
                      id="note-category"
                      value={currentNote.category}
                      onChange={(e) => setCurrentNote(prev => ({ ...prev, category: e.target.value }))}
                      className="w-full px-3 py-2 border rounded-md bg-background"
                    >
                      {categories.map(category => (
                        <option key={category} value={category}>{category}</option>
                      ))}
                    </select>
                  </div>

                  <div>
                    <Label htmlFor="new-tag">Add Tags</Label>
                    <div className="flex space-x-2">
                      <Input
                        id="new-tag"
                        value={newTag}
                        onChange={(e) => setNewTag(e.target.value)}
                        placeholder="Enter tag..."
                        onKeyPress={(e) => e.key === 'Enter' && addTag()}
                      />
                      <Button onClick={addTag} size="sm">
                        <Tag size={16} />
                      </Button>
                    </div>
                  </div>
                </div>

                {currentNote.tags.length > 0 && (
                  <div>
                    <Label>Tags</Label>
                    <div className="flex flex-wrap gap-2 mt-2">
                      {currentNote.tags.map((tag, index) => (
                        <Badge
                          key={tag}
                          className={`${getTagColor(index)} cursor-pointer`}
                          onClick={() => removeTag(tag)}
                        >
                          {tag} Ã—
                        </Badge>
                      ))}
                    </div>
                  </div>
                )}

                <div>
                  <Label htmlFor="note-content">Content</Label>
                  <Textarea
                    id="note-content"
                    value={currentNote.content}
                    onChange={(e) => setCurrentNote(prev => ({ ...prev, content: e.target.value }))}
                    placeholder="Start writing your note..."
                    rows={15}
                    className="font-mono"
                  />
                </div>

                {currentNote.createdAt && (
                  <div className="text-xs text-muted-foreground">
                    Created: {currentNote.createdAt} | Updated: {currentNote.updatedAt}
                  </div>
                )}
              </CardContent>
            </Card>
          </div>

          {/* Statistics */}
          {notes.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle>Statistics</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                  <div className="text-center p-4 bg-primary/5 rounded-lg">
                    <div className="text-2xl font-bold text-primary">{notes.length}</div>
                    <div className="text-sm text-muted-foreground">Total Notes</div>
                  </div>

                  <div className="text-center p-4 bg-green-500/5 rounded-lg">
                    <div className="text-2xl font-bold text-green-600">
                      {notes.reduce((total, note) => total + note.content.split(' ').length, 0)}
                    </div>
                    <div className="text-sm text-muted-foreground">Total Words</div>
                  </div>

                  <div className="text-center p-4 bg-blue-500/5 rounded-lg">
                    <div className="text-2xl font-bold text-blue-600">
                      {[...new Set(notes.flatMap(note => note.tags))].length}
                    </div>
                    <div className="text-sm text-muted-foreground">Unique Tags</div>
                  </div>

                  <div className="text-center p-4 bg-purple-500/5 rounded-lg">
                    <div className="text-2xl font-bold text-purple-600">
                      {[...new Set(notes.map(note => note.category))].length}
                    </div>
                    <div className="text-sm text-muted-foreground">Categories</div>
                  </div>
                </div>
              </CardContent>
            </Card>
          )}
        </div>

        {/* Ad Slot */}
        <div className="mt-8">
          <AdSlot position="note-taking-bottom" page="productivity-tools" size="banner" />
        </div>
      </div>
    </div>
  );
}

