import { useEffect, useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Copy, ArrowLeftRight, RefreshCw } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import AdSlot from "@/components/AdSlot";

// Conversion factors to base units
const conversions = {
  length: {
    name: "Length",
    units: {
      meters: { name: "Meters", factor: 1 },
      kilometers: { name: "Kilometers", factor: 1000 },
      centimeters: { name: "Centimeters", factor: 0.01 },
      millimeters: { name: "Millimeters", factor: 0.001 },
      inches: { name: "Inches", factor: 0.0254 },
      feet: { name: "Feet", factor: 0.3048 },
      yards: { name: "Yards", factor: 0.9144 },
      miles: { name: "Miles", factor: 1609.344 },
    }
  },
  weight: {
    name: "Weight",
    units: {
      grams: { name: "Grams", factor: 1 },
      kilograms: { name: "Kilograms", factor: 1000 },
      pounds: { name: "Pounds", factor: 453.592 },
      ounces: { name: "Ounces", factor: 28.3495 },
      tons: { name: "Tons", factor: 1000000 },
      stones: { name: "Stones", factor: 6350.29 },
    }
  },
  temperature: {
    name: "Temperature",
    units: {
      celsius: { name: "Celsius", factor: 1 },
      fahrenheit: { name: "Fahrenheit", factor: 1 },
      kelvin: { name: "Kelvin", factor: 1 },
    }
  },
  volume: {
    name: "Volume",
    units: {
      liters: { name: "Liters", factor: 1 },
      milliliters: { name: "Milliliters", factor: 0.001 },
      gallons: { name: "Gallons (US)", factor: 3.78541 },
      quarts: { name: "Quarts", factor: 0.946353 },
      pints: { name: "Pints", factor: 0.473176 },
      cups: { name: "Cups", factor: 0.236588 },
      fluid_ounces: { name: "Fluid Ounces", factor: 0.0295735 },
    }
  },
  area: {
    name: "Area",
    units: {
      square_meters: { name: "Square Meters", factor: 1 },
      square_kilometers: { name: "Square Kilometers", factor: 1000000 },
      square_feet: { name: "Square Feet", factor: 0.092903 },
      square_inches: { name: "Square Inches", factor: 0.00064516 },
      acres: { name: "Acres", factor: 4046.86 },
      hectares: { name: "Hectares", factor: 10000 },
    }
  },
  speed: {
    name: "Speed",
    units: {
      meters_per_second: { name: "Meters/Second", factor: 1 },
      kilometers_per_hour: { name: "Kilometers/Hour", factor: 0.277778 },
      miles_per_hour: { name: "Miles/Hour", factor: 0.44704 },
      feet_per_second: { name: "Feet/Second", factor: 0.3048 },
      knots: { name: "Knots", factor: 0.514444 },
    }
  }
};

export default function UnitConverter() {
  const { toast } = useToast();
  const [category, setCategory] = useState("length");
  const [fromUnit, setFromUnit] = useState("meters");
  const [toUnit, setToUnit] = useState("feet");
  const [fromValue, setFromValue] = useState("");
  const [toValue, setToValue] = useState("");

  useEffect(() => {
    document.title = "Unit Converter - ToolSuite Pro | Convert Length, Weight, Temperature & More";

    const metaDescription = document.querySelector('meta[name="description"]');
    if (metaDescription) {
      metaDescription.setAttribute('content', 'Professional unit converter for length, weight, temperature, volume, area, and speed. Accurate conversions with real-time results for engineering and everyday use.');
    }

    // Add structured data
    const structuredData = {
      "@context": "https://schema.org",
      "@type": "WebApplication",
      "name": "Unit Converter",
      "description": "Professional unit converter tool",
      "applicationCategory": "UtilityApplication",
      "operatingSystem": "Web Browser",
      "offers": {
        "@type": "Offer",
        "price": "0",
        "priceCurrency": "USD"
      }
    };

    const script = document.createElement('script');
    script.type = 'application/ld+json';
    script.text = JSON.stringify(structuredData);
    document.head.appendChild(script);

    return () => {
      document.head.removeChild(script);
    };
  }, []);

  const convertValue = (value: string, from: string, to: string, cat: string) => {
    if (!value || isNaN(parseFloat(value))) return "";

    const numValue = parseFloat(value);
    const categoryData = conversions[cat as keyof typeof conversions];

    if (cat === "temperature") {
      return convertTemperature(numValue, from, to).toFixed(6).replace(/\.?0+$/, "");
    }

    const fromFactor = categoryData.units[from as keyof typeof categoryData.units]?.factor || 1;
    const toFactor = categoryData.units[to as keyof typeof categoryData.units]?.factor || 1;

    const baseValue = numValue * fromFactor;
    const convertedValue = baseValue / toFactor;

    return convertedValue.toFixed(6).replace(/\.?0+$/, "");
  };

  const convertTemperature = (value: number, from: string, to: string): number => {
    let celsius = value;

    // Convert to Celsius first
    if (from === "fahrenheit") {
      celsius = (value - 32) * 5/9;
    } else if (from === "kelvin") {
      celsius = value - 273.15;
    }

    // Convert from Celsius to target
    if (to === "fahrenheit") {
      return celsius * 9/5 + 32;
    } else if (to === "kelvin") {
      return celsius + 273.15;
    }

    return celsius;
  };

  useEffect(() => {
    if (fromValue) {
      const result = convertValue(fromValue, fromUnit, toUnit, category);
      setToValue(result);
    }
  }, [fromValue, fromUnit, toUnit, category]);

  const handleCategoryChange = (newCategory: string) => {
    setCategory(newCategory);
    const firstUnit = Object.keys(conversions[newCategory as keyof typeof conversions].units)[0];
    const secondUnit = Object.keys(conversions[newCategory as keyof typeof conversions].units)[1];
    setFromUnit(firstUnit);
    setToUnit(secondUnit);
    setFromValue("");
    setToValue("");
  };

  const swapUnits = () => {
    const tempUnit = fromUnit;
    setFromUnit(toUnit);
    setToUnit(tempUnit);
    setFromValue(toValue);
    setToValue(fromValue);
  };

  const copyResult = () => {
    navigator.clipboard.writeText(toValue);
    toast({
      title: "Copied!",
      description: "Conversion result copied to clipboard",
    });
  };

  const clearAll = () => {
    setFromValue("");
    setToValue("");
  };

  return (
    <div className="min-h-screen py-8 px-4">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center mb-4">
            <div className="gradient-bg p-3 rounded-lg mr-4">
              <ArrowLeftRight className="text-white" size={32} />
            </div>
            <h1 className="text-4xl md:text-5xl font-bold gradient-text">Unit Converter</h1>
          </div>
          <p className="text-muted-foreground text-lg max-w-2xl mx-auto">
            Convert between different units of measurement with precision and ease. 
            Support for length, weight, temperature, volume, area, and speed conversions.
          </p>
        </div>

        {/* Ad Slot */}
        <AdSlot position="unit-converter-top" size="banner" />

        <div className="space-y-8">
          {/* Category Selection */}
          <Card>
            <CardHeader>
              <CardTitle>Select Conversion Category</CardTitle>
            </CardHeader>
            <CardContent>
              <Tabs value={category} onValueChange={handleCategoryChange}>
                <TabsList className="grid w-full grid-cols-3 lg:grid-cols-6">
                  {Object.entries(conversions).map(([key, value]) => (
                    <TabsTrigger key={key} value={key} className="text-xs lg:text-sm">
                      {value.name}
                    </TabsTrigger>
                  ))}
                </TabsList>
              </Tabs>
            </CardContent>
          </Card>

          {/* Converter Interface */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* From Unit */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  From
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={swapUnits}
                    className="ml-auto"
                  >
                    <ArrowLeftRight size={16} />
                  </Button>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label htmlFor="from-unit">Unit</Label>
                  <Select value={fromUnit} onValueChange={setFromUnit}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {Object.entries(conversions[category as keyof typeof conversions].units).map(([key, unit]) => (
                        <SelectItem key={key} value={key}>
                          {unit.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <Label htmlFor="from-value">Value</Label>
                  <Input
                    id="from-value"
                    type="number"
                    value={fromValue}
                    onChange={(e) => setFromValue(e.target.value)}
                    placeholder="Enter value to convert"
                    className="text-lg"
                  />
                </div>
              </CardContent>
            </Card>

            {/* To Unit */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  To
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={copyResult}
                    className="ml-auto"
                    disabled={!toValue}
                  >
                    <Copy size={16} />
                  </Button>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label htmlFor="to-unit">Unit</Label>
                  <Select value={toUnit} onValueChange={setToUnit}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {Object.entries(conversions[category as keyof typeof conversions].units).map(([key, unit]) => (
                        <SelectItem key={key} value={key}>
                          {unit.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <Label htmlFor="to-value">Result</Label>
                  <Input
                    id="to-value"
                    type="text"
                    value={toValue}
                    readOnly
                    placeholder="Conversion result"
                    className="text-lg font-mono bg-muted"
                  />
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Action Buttons */}
          <div className="flex justify-center space-x-4">
            <Button onClick={clearAll} variant="outline">
              <RefreshCw size={16} className="mr-2" />
              Clear
            </Button>
          </div>

          {/* Quick Conversion Table */}
          {fromValue && (
            <Card>
              <CardHeader>
                <CardTitle>Quick Conversions</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {Object.entries(conversions[category as keyof typeof conversions].units).map(([key, unit]) => {
                    const result = convertValue(fromValue, fromUnit, key, category);
                    return (
                      <div key={key} className="p-3 border rounded-lg">
                        <div className="font-medium">{unit.name}</div>
                        <div className="text-lg font-mono">{result}</div>
                      </div>
                    );
                  })}
                </div>
              </CardContent>
            </Card>
          )}
        </div>

        {/* Ad Slot */}
        <div className="mt-8">
          <AdSlot position="unit-converter-bottom" size="banner" />
        </div>
      </div>
    </div>
  );
}

force_db_migration.js.content:
import postgres from 'postgres';

const sql = postgres(process.env.DATABASE_URL);

async function forceMigration() {
  try {
    // Create all required tables for ad management system

    // Create ad_providers table
    await sql`
      CREATE TABLE IF NOT EXISTS ad_providers (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        name TEXT NOT NULL UNIQUE,
        type TEXT NOT NULL,
        is_active BOOLEAN NOT NULL DEFAULT true,
        credentials TEXT,
        settings JSONB,
        created_at TIMESTAMP DEFAULT NOW() NOT NULL,
        updated_at TIMESTAMP DEFAULT NOW() NOT NULL
      )
    `;

    // Create ad_campaigns table  
    await sql`
      CREATE TABLE IF NOT EXISTS ad_campaigns (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        name TEXT NOT NULL,
        provider_id VARCHAR NOT NULL,
        ad_type TEXT NOT NULL,
        ad_code TEXT NOT NULL,
        dimensions TEXT,
        is_active BOOLEAN NOT NULL DEFAULT true,
        start_date TIMESTAMP,
        end_date TIMESTAMP,
        target_audience JSONB,
        cpm_rate INTEGER,
        click_url TEXT,
        created_at TIMESTAMP DEFAULT NOW() NOT NULL,
        updated_at TIMESTAMP DEFAULT NOW() NOT NULL
      )
    `;

    // Create ad_slot_assignments table
    await sql`
      CREATE TABLE IF NOT EXISTS ad_slot_assignments (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        slot_id VARCHAR NOT NULL,
        campaign_id VARCHAR NOT NULL,
        is_active BOOLEAN NOT NULL DEFAULT true,
        priority INTEGER NOT NULL DEFAULT 1,
        assigned_at TIMESTAMP DEFAULT NOW() NOT NULL,
        assigned_by VARCHAR NOT NULL
      )
    `;

    // Create ad_views table
    await sql`
      CREATE TABLE IF NOT EXISTS ad_views (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        slot_id VARCHAR NOT NULL,
        campaign_id VARCHAR,
        session_id TEXT NOT NULL,
        ip_hash TEXT NOT NULL,
        user_agent TEXT,
        page TEXT NOT NULL,
        view_type TEXT NOT NULL,
        timestamp TIMESTAMP DEFAULT NOW() NOT NULL
      )
    `;

    // Create ad_analytics table
    await sql`
      CREATE TABLE IF NOT EXISTS ad_analytics (
        id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
        slot_id VARCHAR NOT NULL,
        campaign_id VARCHAR,
        date TEXT NOT NULL,
        page TEXT NOT NULL,
        impressions INTEGER NOT NULL DEFAULT 0,
        clicks INTEGER NOT NULL DEFAULT 0,
        revenue INTEGER NOT NULL DEFAULT 0,
        unique_views INTEGER NOT NULL DEFAULT 0,
        created_at TIMESTAMP DEFAULT NOW() NOT NULL
      )
    `;

    console.log('Complete ad management database migration completed successfully');
    process.exit(0);
  } catch (error) {
    console.error('Migration error:', error);
    process.exit(1);
  }
}

forceMigration();

client/src/components/SearchResults.tsx.content:
import { useState, useEffect, useRef } from "react";
import { Link } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Tool } from "@/lib/searchData";
import { Search, ExternalLink, FileText, Image, Volume2, Type, Briefcase } from "lucide-react";

interface SearchResultsProps {
  results: Tool[];
  isVisible: boolean;
  onClose: () => void;
  searchQuery: string;
}

const getCategoryIcon = (category: string) => {
  switch (category.toLowerCase()) {
    case 'pdf':
      return <FileText className="w-4 h-4" />;
    case 'image':
      return <Image className="w-4 h-4" />;
    case 'audio':
      return <Volume2 className="w-4 h-4" />;
    case 'text':
      return <Type className="w-4 h-4" />;
    case 'productivity':
      return <Briefcase className="w-4 h-4" />;
    default:
      return <Search className="w-4 h-4" />;
  }
};

const getCategoryColor = (category: string) => {
  switch (category.toLowerCase()) {
    case 'pdf':
      return 'bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-400';
    case 'image':
      return 'bg-blue-100 text-blue-800 dark:bg-blue-900/20 dark:text-blue-400';
    case 'audio':
      return 'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-400';
    case 'text':
      return 'bg-purple-100 text-purple-800 dark:bg-purple-900/20 dark:text-purple-400';
    case 'productivity':
      return 'bg-orange-100 text-orange-800 dark:bg-orange-900/20 dark:text-orange-400';
    default:
      return 'bg-gray-100 text-gray-800 dark:bg-gray-900/20 dark:text-gray-400';
  }
};

export default function SearchResults({ results, isVisible, onClose, searchQuery }: SearchResultsProps) {
  const resultsRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (resultsRef.current && !resultsRef.current.contains(event.target as Node)) {
        onClose();
      }
    };

    const handleEscape = (event: KeyboardEvent) => {
      if (event.key === 'Escape') {
        onClose();
      }
    };

    if (isVisible) {
      document.addEventListener('mousedown', handleClickOutside);
      document.addEventListener('keydown', handleEscape);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleEscape);
    };
  }, [isVisible, onClose]);

  if (!isVisible) return null;

  return (
    <div className="fixed inset-0 z-50 bg-black/20 backdrop-blur-sm">
      <div className="flex items-start justify-center pt-20 px-4">
        <div 
          ref={resultsRef}
          className="w-full max-w-4xl glassmorphism rounded-xl border border-white/20 max-h-[70vh] overflow-hidden"
        >
          {/* Header */}
          <div className="p-4 border-b border-white/10">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-2">
                <Search className="w-5 h-5 text-primary" />
                <h3 className="text-lg font-semibold">
                  Search Results for "{searchQuery}"
                </h3>
              </div>
              <Button
                variant="ghost"
                size="sm"
                onClick={onClose}
                className="text-muted-foreground hover:text-foreground"
              >
                âœ•
              </Button>
            </div>
            <p className="text-sm text-muted-foreground mt-1">
              Found {results.length} tool{results.length !== 1 ? 's' : ''}
            </p>
          </div>

          {/* Results */}
          <div className="overflow-y-auto max-h-[50vh] p-4">
            {results.length === 0 ? (
              <div className="text-center py-8">
                <Search className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                <h4 className="text-lg font-medium mb-2">No tools found</h4>
                <p className="text-muted-foreground">
                  Try searching for "PDF", "Image", "Audio", "Text", or "Productivity" tools
                </p>
              </div>
            ) : (
              <div className="grid gap-3">
                {results.map((tool) => (
                  <Link
                    key={tool.id}
                    href={tool.route}
                    onClick={onClose}
                  >
                    <Card className="cursor-pointer hover:shadow-md transition-all duration-200 hover:bg-card/80 border-white/10 bg-card/50">
                      <CardContent className="p-4">
                        <div className="flex items-start justify-between">
                          <div className="flex-1">
                            <div className="flex items-center space-x-2 mb-2">
                              <Badge 
                                variant="secondary" 
                                className={`text-xs ${getCategoryColor(tool.category)}`}
                              >
                                <span className="flex items-center space-x-1">
                                  {getCategoryIcon(tool.category)}
                                  <span>{tool.category}</span>
                                </span>
                              </Badge>
                            </div>
                            <h4 className="font-semibold text-foreground mb-1 hover:text-primary transition-colors">
                              {tool.name}
                            </h4>
                            <p className="text-sm text-muted-foreground line-clamp-2">
                              {tool.description}
                            </p>
                            <div className="flex flex-wrap gap-1 mt-2">
                              {tool.keywords.slice(0, 4).map((keyword) => (
                                <span 
                                  key={keyword}
                                  className="text-xs px-2 py-1 bg-muted/50 rounded-md text-muted-foreground"
                                >
                                  {keyword}
                                </span>
                              ))}
                              {tool.keywords.length > 4 && (
                                <span className="text-xs text-muted-foreground">
                                  +{tool.keywords.length - 4} more
                                </span>
                              )}
                            </div>
                          </div>
                          <ExternalLink className="w-4 h-4 text-muted-foreground ml-4" />
                        </div>
                      </CardContent>
                    </Card>
                  </Link>
                ))}
              </div>
            )}
          </div>

          {/* Footer */}
          {results.length > 0 && (
            <div className="p-4 border-t border-white/10 text-center">
              <p className="text-xs text-muted-foreground">
                Click on any tool to start using it instantly
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

